class Ideone
{
	public static void show(int n, int acc)
	{
		if (n &lt; 0) n = 65536 + n;
		if (acc == 0) return;
		show(n &gt;&gt; 1, acc - 1);
		System.out.print(n % 2);
	}
	public static void main (String[] args) throws java.lang.Exception
	{
		System.out.print("5 the bits is ");
		show(5, 16);
		System.out.println();
		System.out.print("-5 the bits is ");
		show(-5, 16);
		System.out.println();
	}
}
