#include "stdio.h"

class Machine
{
public:
    char Play()
    {
        return 'R';
    }
};

class Human
{
public:
    char Play()
    {
        char ch;
		do {
        scanf("%c", &amp;ch);
		} while (ch == ' ');
        return ch;
    }
};

class Referee
{
public:
    char Judge(char m, char p)
    {
        if (m == p) return 'T';
        if ((m == 'S' &amp;&amp; p == 'P') ||
            (m == 'P' &amp;&amp; p == 'R') ||
            (m == 'R' &amp;&amp; p == 'S'))
            return 'L';
        else
            return 'W';
    }
};

int main()
{
    Machine m;
    Human p;
    Referee r;
    int n;
    char buf[100];
    scanf("%d", &amp;n);
    for (int i = 0; i &lt; n * 2; i += 2)
	{
        buf[i] = r.Judge(m.Play(), p.Play());
		buf[i + 1] = ' ';
	}
    buf[n * 2 - 1] = '\0';
    printf("%s\n", buf);
    return 0;
}
