    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d%d&amp;#34;&lt;/span&gt;, &amp;amp;n, &amp;amp;m) !&amp;#61; EOF)
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (m &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; &amp;amp;&amp;amp; n &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
            &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;
        a[n] &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; n; i&amp;#43;&amp;#43;)
            &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;, &amp;amp;a[i]);
        a[n] &amp;#61; m;
        &lt;span class="hljs-built_in"&gt;qsort&lt;/span&gt;(a, n &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;, &lt;span class="hljs-built_in"&gt;sizeof&lt;/span&gt;(a[&lt;span class="hljs-number"&gt;0&lt;/span&gt;]), cmp);
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; n &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i&amp;#43;&amp;#43;)
        {
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (i &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
                &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;, a[&lt;span class="hljs-number"&gt;0&lt;/span&gt;]);
            &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
                &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34; %d&amp;#34;&lt;/span&gt;, a[i]);
        }
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;\n&amp;#34;&lt;/span&gt;);
    }
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

&lt;/code&gt;&lt;/pre&gt;
