&lt;div class="post-text" itemprop="text"&gt;
&lt;p&gt;You could use channel(safe for concurrent use) and wait group to read from queue concurrently&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;package main

import (
    "fmt"
    "sync"
)

func main() {
    queue := make(chan int)

    wg := new(sync.WaitGroup)
    wg.Add(1)
    defer wg.Wait()

    go func(wg *sync.WaitGroup) {
        for {

            r, ok := &amp;lt;-queue
            if !ok {
                wg.Done()
                return
            }

            fmt.Println(r)
        }
    }(wg)

    for i := 1; i &amp;lt;= 10; i++ {
        queue &amp;lt;- i
    }

    close(queue)
}
&lt;/code&gt;&lt;/pre&gt;

