&lt;p&gt;代码如下&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; _CRT_SECURE_NO_WARNINGS 1&lt;/span&gt;



&lt;span class="hljs-comment"&gt;//模块ID&amp;#xff08;1字节&amp;#xff09;&lt;/span&gt;
&lt;span class="hljs-comment"&gt;//数据头&amp;#xff08;1字节&amp;#xff0c;包含6个数据的bit0&amp;#43;二级IDbit0&amp;#xff0c;顺序从高到低依次为&amp;#xff1a;数据1bit0 数据2bit0....二级IDbit0&amp;#xff0c;最高位bit7&amp;#61;1&amp;#xff09;&lt;/span&gt;
&lt;span class="hljs-comment"&gt;//二级ID&amp;#xff08;1字节&amp;#xff0c;bit0置1&amp;#xff09;&lt;/span&gt;
&lt;span class="hljs-comment"&gt;//数据6字节&amp;#xff08;bit0置1&amp;#xff09;&amp;#43;干扰码6字节&amp;#xff08;交替排列&amp;#xff09;&lt;/span&gt;
&lt;span class="hljs-comment"&gt;//校验码&amp;#xff08;1字节&amp;#xff09;&amp;#xff08;数据高7位和&amp;#xff0c;包括模块ID高7位&amp;#43;二级ID高7位&amp;#43;数据头高7位&amp;#xff09;&lt;/span&gt;
&lt;span class="hljs-comment"&gt;//总长度4&amp;#43;12&amp;#61;16个字节&lt;/span&gt;

&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;iostream&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;string&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;using&lt;/span&gt; &lt;span class="hljs-keyword"&gt;namespace&lt;/span&gt; std;
&lt;span class="hljs-comment"&gt;/**&amp;#64;brief 编码
  &amp;#64;param[in] modId 模块ID
  &amp;#64;param[in] subId 二级ID
  &amp;#64;param[in] 6字节数据
  &amp;#64;param[in] 6字节干扰码
  &amp;#64;return 编码后的字符串
  */&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;char&lt;/span&gt;* &lt;span class="hljs-title"&gt;enCode&lt;/span&gt;&lt;span class="hljs-params"&gt;( &lt;span class="hljs-type"&gt;char&lt;/span&gt; modId, &lt;span class="hljs-type"&gt;char&lt;/span&gt; subId, &lt;span class="hljs-type"&gt;char&lt;/span&gt; data[], &lt;span class="hljs-type"&gt;char&lt;/span&gt; gr[] )&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; k &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-type"&gt;char&lt;/span&gt;* package &amp;#61; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-type"&gt;char&lt;/span&gt;[&lt;span class="hljs-number"&gt;17&lt;/span&gt;];
    package[&lt;span class="hljs-number"&gt;16&lt;/span&gt;] &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; 

    &lt;span class="hljs-comment"&gt;//模块ID&lt;/span&gt;
    package[&lt;span class="hljs-number"&gt;0&lt;/span&gt;] &amp;#61; modId;

    &lt;span class="hljs-comment"&gt;//数据头&lt;/span&gt;
    &lt;span class="hljs-type"&gt;char&lt;/span&gt; t &amp;#61; &lt;span class="hljs-number"&gt;0x80&lt;/span&gt;; 
    &lt;span class="hljs-type"&gt;char&lt;/span&gt; m;
    &lt;span class="hljs-comment"&gt;//1.拼接6个数据的bit0&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-number"&gt;6&lt;/span&gt;; i&amp;#43;&amp;#43;)
    {
        m &amp;#61; (data[i] &amp;amp; &lt;span class="hljs-number"&gt;0x01&lt;/span&gt;);&lt;span class="hljs-comment"&gt;//得到data[i]的bit0&lt;/span&gt;
        m &amp;#61; m &amp;lt;&amp;lt; (&lt;span class="hljs-number"&gt;6&lt;/span&gt; - i);    &lt;span class="hljs-comment"&gt;//m左移6-i位&lt;/span&gt;
        t &amp;#43;&amp;#61; m;
    }
    &lt;span class="hljs-comment"&gt;//2.拼接二级ID 的bit0&lt;/span&gt;
    m &amp;#61; (subId &amp;amp; &lt;span class="hljs-number"&gt;0x01&lt;/span&gt;);
    t &amp;#43;&amp;#61; m;
    package[&lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#61; t;

    &lt;span class="hljs-comment"&gt;//二级ID&lt;/span&gt;
    package[&lt;span class="hljs-number"&gt;2&lt;/span&gt;] &amp;#61; subId | &lt;span class="hljs-number"&gt;0x01&lt;/span&gt;; &lt;span class="hljs-comment"&gt;//二级ID的bit0置1&lt;/span&gt;

    &lt;span class="hljs-comment"&gt;//数据&amp;#43;干扰码&lt;/span&gt;
    k &amp;#61; &lt;span class="hljs-number"&gt;3&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-number"&gt;6&lt;/span&gt;; i&amp;#43;&amp;#43;)
    {
        m &amp;#61; data[i] | &lt;span class="hljs-number"&gt;0x01&lt;/span&gt;; &lt;span class="hljs-comment"&gt;//数据的bit0置1&lt;/span&gt;
        package[k&amp;#43;&amp;#43;] &amp;#61; m;
        package[k&amp;#43;&amp;#43;] &amp;#61; gr[i]; &lt;span class="hljs-comment"&gt;//干扰码&lt;/span&gt;
