&lt;p&gt;If I well understood you — you need &lt;a href="/questions/tagged/openid" class="post-tag" title="show questions tagged 'openid'" rel="tag"&gt;openid&lt;/a&gt;, not &lt;a href="/questions/tagged/oath" class="post-tag" title="show questions tagged 'oath'" rel="tag"&gt;oath&lt;/a&gt;.
I rewrote python example (&lt;a href="http://code.google.com/appengine/articles/openid.html#model_python"&gt;Federated login and logout&lt;/a&gt;) for go-lang. Hope this help.&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;package gae_go_openid_demo

import (
    "fmt"
    "os"
    "http"

    "appengine"
    "appengine/user"
)

func init() {
    http.HandleFunc("/", hello)
    http.HandleFunc("/_ah/login_required", openIdHandler)
}

func hello(w http.ResponseWriter, r *http.Request) {
    c := appengine.NewContext(r)
    u := user.Current(c)
    if u != nil {
        url, err := user.LogoutURL(c, "/")
        check(err);
        fmt.Fprintf(w, "Hello, %s! (&amp;lt;a href='%s'&amp;gt;Sign out&amp;lt;/a&amp;gt;)", u, url)
    } else {
        fmt.Fprintf(w, "Please, &amp;lt;a href='/_ah/login_required'&amp;gt;login&amp;lt;/a&amp;gt;.")
    }

}

func openIdHandler(w http.ResponseWriter, r *http.Request) {
    providers := map[string]string {
        "Google"   : "www.google.com/accounts/o8/id", // shorter alternative: "Gmail.com"
        "Yahoo"    : "yahoo.com",
        "MySpace"  : "myspace.com",
        "AOL"      : "aol.com",
        "MyOpenID" : "myopenid.com",
        // add more here
    }

    c := appengine.NewContext(r)
    fmt.Fprintf(w, "Sign in at: ")
    for name, url := range providers {
        login_url, err := user.LoginURLFederated(c, "/", url)
        check(err);
        fmt.Fprintf(w, "[&amp;lt;a href='%s'&amp;gt;%s&amp;lt;/a&amp;gt;]", login_url, name)
    }
}

// check aborts the current execution if err is non-nil.
func check(err os.Error) {
    if err != nil {
        panic(err)
    }
}
&lt;/code&gt;&lt;/pre&gt;
    &lt;/div&gt;

