            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (prices&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;secondDealSell&lt;/span&gt; - &lt;span class="hljs-number"&gt;1&lt;/span&gt;]&lt;/span&gt; &amp;lt; prices&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;secondDealSell&lt;/span&gt;]&lt;/span&gt;) {
                break;
            }
        }
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (firstDealSell &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; firstDealSell &amp;lt; prices.length; firstDealSell&amp;#43;&amp;#43;) {
            &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (firstDealSell &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt; &amp;lt; prices.length&lt;span class="hljs-operator"&gt; &amp;amp;&amp;amp; &lt;/span&gt;prices&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;firstDealSell&lt;/span&gt; &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;]&lt;/span&gt; &amp;gt;&amp;#61; prices&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;firstDealSell&lt;/span&gt;]&lt;/span&gt;) {
                firstDealSell&amp;#43;&amp;#43;;
            }
            &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; result1 &amp;#61; max&lt;span class="hljs-constructor"&gt;Profit(&lt;span class="hljs-params"&gt;prices&lt;/span&gt;, 0, &lt;span class="hljs-params"&gt;firstDealSell&lt;/span&gt;)&lt;/span&gt;;
            &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; result2 &amp;#61; max&lt;span class="hljs-constructor"&gt;Profit(&lt;span class="hljs-params"&gt;prices&lt;/span&gt;, &lt;span class="hljs-params"&gt;firstDealSell&lt;/span&gt; &amp;#43; 1, &lt;span class="hljs-params"&gt;secondDealSell&lt;/span&gt;)&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (result1 &amp;#43; result2 &amp;gt; result) {
                result &amp;#61; result1 &amp;#43; result2;
            }
        }
        return result;
    }
    &lt;span class="hljs-keyword"&gt;private&lt;/span&gt; &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; max&lt;span class="hljs-constructor"&gt;Profit(&lt;span class="hljs-params"&gt;int&lt;/span&gt;[] &lt;span class="hljs-params"&gt;prices&lt;/span&gt;, &lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;left&lt;/span&gt;, &lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;right&lt;/span&gt;)&lt;/span&gt; {
        &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; result &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (right - left &amp;lt; &lt;span class="hljs-number"&gt;1&lt;/span&gt;) {
            return result;
        }
        &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; minPrice &amp;#61; prices&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;left&lt;/span&gt;]&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i &amp;#61; left &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i &amp;lt;&amp;#61; right; i&amp;#43;&amp;#43;) {
            result &amp;#61; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Math&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;max(result, prices&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt; - minPrice);
            minPrice &amp;#61; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Math&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;min(minPrice, prices&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;);
        }
        return result;
    }
}
