&lt;pre&gt;&lt;code class="language-java"&gt;
&lt;span class="hljs-class"&gt;&lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title"&gt;Solution&lt;/span&gt; &lt;/span&gt;{
    &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; ListNode reverseBetween(ListNode head, &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; m, &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; n) {
        ListNode dummy &amp;#61; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; ListNode(&lt;span class="hljs-number"&gt;0&lt;/span&gt;);
        dummy.&lt;span class="hljs-keyword"&gt;next&lt;/span&gt; &amp;#61; head;
        ListNode pre &amp;#61; dummy;
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i &amp;lt; m; i&amp;#43;&amp;#43;) {
            pre &amp;#61; pre.&lt;span class="hljs-keyword"&gt;next&lt;/span&gt;;
        }
        head &amp;#61; pre.&lt;span class="hljs-keyword"&gt;next&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i &amp;#61; m; i &amp;lt; n; i&amp;#43;&amp;#43;) {
            ListNode nex &amp;#61; head.&lt;span class="hljs-keyword"&gt;next&lt;/span&gt;;
            head.&lt;span class="hljs-keyword"&gt;next&lt;/span&gt; &amp;#61; nex.&lt;span class="hljs-keyword"&gt;next&lt;/span&gt;;
            nex.&lt;span class="hljs-keyword"&gt;next&lt;/span&gt; &amp;#61; pre.&lt;span class="hljs-keyword"&gt;next&lt;/span&gt;;
