} 
int playGame(int map1[][16], int map2[][16], int sum, int imp) {
	int i, j;
	int x, y;
	char op;
	int num = 0;
	while (1) {
		for (int i = 0; i < 14; i ++ ) {
			for (int j = 0; j < 16; j ++ ) {
				if (map1[i][j] == PERSON) {
					break;
				} 
			}
			if (j < 16) {
				break;
			}
		}
		x = i;
		y = j;
		system("cls");
		num = printMap(map1, imp);
		if (num == sum) {
			printf("Congratulations!\n");
			Sleep(3000);
			return 0;
		}
		else {
		    op = getch();
		    if ((op == 'n') || (op == 'N')) {
				return KEY_NEXT;
	     	}
			else if ((op == 'q') || (op == 'Q')) {
				return KEY_RETURN; 
			}	
		}
		switch(op) {
			case KEY_UP : 
				moveBox(map1, x, y, 1, map2);
				break;
			case KEY_LEFT : 
				moveBox(map1, x, y, 2, map2);
				break;
			case KEY_RIGHT : 
			    moveBox(map1, x, y, 3, map2);
				break;
			case KEY_DOWN : 
				moveBox(map1, x, y, 4, map2); 
				break;
			default : 
				break;     
		}
    }
}
void moveBox(int map1[][16], int x, int y, int select, int map2[][16]) {
	int x1, y1;
	int x2, y2;
	switch(select) {
		case 1 : 
		   x1 = x - 1;
		   y1 = y;
		   x2 = x - 2;
		   y2 = y;
		   break;
		case 2 :
		   x1 = x;
		   y1 = y - 1;
		   x2 = x;
		   y2 = y - 2;
		   break;
		case 3 :
		   x1 = x;
		   y1 = y + 1;
		   x2 = x;
		   y2 = y + 2;
		   break;
		case 4 :
		   x1 = x + 1;
		   y1 = y;
		   x2 = x + 2;
		   y2 = y;
		   break;
		default : 
		   break; 
	}
	switch(map1[x1][y1]) {
		case WALL : 
		     break;
	    case SPACE : 
	    case TARGET : 
		     map1[x1][x2] = PERSON;
			 if (map2[x][y] == TARGET || map2[x][y] == TARGET_IN) {
			 	map1[x][y] = TARGET;
			 } 
			 else {
			 	map1[x][y] = SPACE;
			 }
			 break;
		case BOX : 
		case TARGET_IN : 
		    if (map1[x2][y2] == TARGET)	{
		    	map1[x2][y2] = TARGET_IN;
		    	map1[x1][y1] = PERSON;
			} 
			else if (map1[x2][y2] == SPACE) {
				map1[x2][y2] = BOX;
				map1[x1][y1] = PERSON;
			}
			else {
				 break;
			}
	        if (map2[x][y] == TARGET || map2[x][y] == TARGET_IN) {
	        	map1[x][y] = TARGET;
			}
			else {
				map1[x][y] = SPACE;
			}
			break;
	}
}

