function [Xm,Ym,Zm]&amp;#61;getLegendreSurf(l,m)
&lt;span class="hljs-attribute"&gt;dx&lt;/span&gt;&amp;#61;pi/30;
&lt;span class="hljs-attribute"&gt;col&lt;/span&gt;&amp;#61;0:dx:pi;
