    &lt;span class="hljs-attribute"&gt;Plm&lt;/span&gt;&amp;#61;reshape(Plm(m&amp;#43;1,:,:),size(phi));
end

a&amp;#61;(2*l&amp;#43;1)&lt;span class="hljs-number"&gt;*fac&lt;/span&gt;torial(l-m);
