&lt;span class="hljs-attribute"&gt;C&lt;/span&gt;&amp;#61;sqrt(a/b);
&lt;span class="hljs-attribute"&gt;Ylm&lt;/span&gt;&amp;#61;C.*Plm.*exp(1i*m*phi);

[Xm,Ym,Zm]&amp;#61;sph2cart(phi,pi/2-theta,abs(real(Ylm)));
end
