 #include <stdio.h>
#include <stdlib.h>
#include <windows.h>

int c1 = 0;
int c2 = 0;
int will_wait;

int accnt1 = 0;
int accnt2 = 0;

DWORD WINAPI run1( LPVOID p ) {
    unsigned long counter = 0;
    int tmp1, tmp2, r;
    int flag = 1;

    DWORD start, end;

    start = GetTickCount();
    while(flag) {

        c1 = 1;
        will_wait = 1;

        while(c2 && (will_wait == 1));
        // wait loop

        r = rand();
        tmp1 = accnt1;
        tmp2 = accnt2;

        accnt1 = tmp1 + r;
        accnt2 = tmp2 - r;
        counter++;

        if (counter >= 10000000 || accnt1 + accnt2 != 0)
            flag = 0;

        c1 = 0;
    }

    end = GetTickCount();
    printf("Thread %lu:\n", GetCurrentThreadId());
    printf("counter = %lu, execution time = %lu ms\n", counter, (end - start));

    return 0;
}

DWORD WINAPI run2( LPVOID p ) {
    unsigned long counter = 0;
    int tmp1, tmp2, r;
    int flag = 1;

    DWORD start, end;

    start = GetTickCount();
    while(flag) {

        c2 = 1;
        will_wait = 2;

        while(c1 && (will_wait == 2));
        // wait loop

        r = rand();
        tmp1 = accnt1;
        tmp2 = accnt2;

        accnt1 = tmp1 + r;
        accnt2 = tmp2 - r;
        counter++;

        if (counter >= 10000000 || accnt1 + accnt2 != 0)
            flag = 0;

        c2 = 0;
    }

    end = GetTickCount();
    printf("Thread %lu:\n", GetCurrentThreadId());
    printf("counter = %lu, execution time = %lu ms\n", counter, (end - start));

    return 0;
}

int main() {
    HANDLE hFirst = CreateThread(NULL, 0, run1, NULL, 0, NULL);
    HANDLE hSecond = CreateThread(NULL, 0, run2, NULL, 0, NULL);

    // CPU affinity
    SetThreadAffinityMask(hFirst, 1);
    SetThreadAffinityMask(hSecond, 1);

    // INFINITE
    WaitForSingleObject(hFirst, INFINITE);
    WaitForSingleObject(hSecond, INFINITE);

    system("PAUSE");

    return 0;
}


