void sort (struct student students[]){
int i,j,x,k,maxsum;x=1;
for(i=0;i<5;i++)
    students[i].rank=0;
for(i=0;i<4;i++)
{
    if(students[i].rank==0)
    {
        maxsum=sum[i];
        for(j=i+1;j<5;j++)
        {
            if(students[j].rank==0)
                if(sum[j]<maxsum)
                {
                    maxsum=sum[j]; k=j;
                }
        }
        students[k].rank=x;  x++;
    }
}
}

