public static void createZipFile(File textFile, String zipFileName,
        String password) {

    byte b[] = new byte[1024];
    ZipOutputStream zout = null;
    InputStream in = null;

    try {
        zout = new ZipOutputStream(new FileOutputStream(zipFileName));
        in = new FileInputStream(textFile);

        String filename = textFile.getName();// å–å¾—æ–‡ä»¶å
        ZipEntry e = new ZipEntry(filename); // å‹ç¼©åä¸å¸¦è·¯å¾„
        zout.putNextEntry(e);

        int len = 0;
        while ((len = in.read(b)) != -1) {
            zout.write(b, 0, len);
        }

        zout.closeEntry();
        zout.flush();
        zout.close();

        in.close();

    } catch (FileNotFoundException e) {
        e.printStackTrace();
    } catch (IOException io) {
        io.printStackTrace();
    } finally {
        if (zout != null) {
            try {
                zout.flush();
                zout.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        if (in != null) {
            try {
                in.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    encryptZipFile(textFile, zipFileName, password);
}

