class Queue
{
private:
    node *rear;
    node *front;
protected:
    int count;//è®¡æ•°å™¨ï¼Œç»Ÿè®¡ç»“ç‚¹ä¸ªæ•°å³çº¿æ€§é˜Ÿåˆ—çš„é•¿åº¦
public:
    Queue();//æ„é€ å‡½æ•°
    ~Queue();//ææ„å‡½æ•°
    void clear(void);//æ¸…ç©ºé“¾é˜Ÿ
    bool empty(void) const;//åˆ¤æ–­æ˜¯å¦ç©ºé˜Ÿ
    bool retrieve(int &item) const;//è¯»å–é˜Ÿå¤´
    bool append(const int &item);//æ•°æ®å…¥é˜Ÿ
    bool serve();//æ•°æ®å‡ºé˜Ÿ
};

/*
ç±»Queueçš„å®ç°éƒ¨åˆ†
*/
Queue::Queue()//æ„é€ å‡½æ•°
{
    front=new node;//ç”³è¯·æ–°ç»“ç‚¹ï¼Œä½œä¸ºé˜Ÿå¤´ç»“ç‚¹
    front->next=NULL;
    rear=front;//é˜Ÿå°¾æŒ‡é’ˆæŒ‡å‘é˜Ÿå¤´
    count=0;//è®¡æ•°å™¨æ¸…é›¶ï¼Œé˜Ÿåˆ—å¼€å§‹æ—¶æ²¡æœ‰å®é™…æ•°æ®
}

Queue::~Queue()//ææ„å‡½æ•°
{
    clear();//åˆ é™¤æ‰€æœ‰æ•°æ®ï¼Œé‡Šæ”¾æ‰€æœ‰ç»“ç‚¹
    delete front;//æŠŠå¤´ç»“ç‚¹ä¹Ÿé‡Šæ”¾æ‰
    count=0;//è®¡æ•°å™¨æ¸…é›¶ï¼Œé˜Ÿåˆ—å¼€å§‹æ—¶æ²¡æœ‰å®é™…æ•°æ®
}

void Queue::clear(void)//æ¸…ç©ºé“¾é˜Ÿ
{
    node *searchp=front->next,*followp=front;//åˆå§‹åŒ–ä¸¤ä¸ªæŒ‡é’ˆ
    while(searchp!=rear)
    {
        followp=searchp;
        searchp=searchp->next;
        delete followp;
    }
    front->next=NULL;//ä¿ç•™äº†æœ€åä¸€ä¸ªç»“ç‚¹ï¼Œå°±æ˜¯å¤´ç»“ç‚¹ï¼Œå¹¶ä¸”é“¾åŸŸç½®ä¸ºç©º
    rear=front;
    count=0;//è®¡æ•°å™¨ä¹Ÿæ¸…é›¶
}

bool Queue::empty(void) const//åˆ¤æ–­æ˜¯å¦ç©ºé“¾
{
    return count==0?true:false;
}

bool Queue::retrieve(int &item) const//è¯»å–é˜Ÿå¤´
{
    if(empty())//å¤„ç†æ„å¤–
        return false;
    item=front->next->data;//è¿”å›è¯»å–çš„æ•°æ®
    return true;//æœ¬æ¬¡æ“ä½œæˆåŠŸ
}

bool Queue::append(const int &item)//è¿›é˜Ÿ
{
    node *newnodep=new node;
    newnodep->data=item;//ç»™æ•°æ®èµ‹å€¼
    rear->next=newnodep;//è¿™ä¸€æ­¥å¯ä»¥çœ‹å‡ºæœ‰å¤´ç»“ç‚¹
    rear=rear->next;//æ”¹åŠ¨é˜Ÿå°¾æŒ‡é’ˆçš„ä½ç½®
    count++;//è®¡æ•°å™¨åŠ 1
    return true;
}

bool Queue::serve()//å‡ºé˜Ÿ
{
    if(empty())//ç©ºé˜Ÿå¤„ç†
        return false;
    node *tempp=front->next;
//  item=tempp->data;//ä¿å­˜æ•°æ®
    front->next=tempp->next;//æ”¹å˜æŒ‡é’ˆ
    delete tempp;//é‡Šæ”¾è¯¥ç»“ç‚¹
    count--;//è®¡æ•°å™¨å‡1
    return true;
}

