class NumSysConversion
{
private:
    int inter;
    float floater;
    Stack *stack;
    Queue *queue;
public:
    NumSysConversion();//æ„é€ å‡½æ•°
    ~NumSysConversion();//ææ„å‡½æ•°
    bool check(char *array,int number);//æ£€æŸ¥è¾“å…¥æ•°æ®æ˜¯å¦ç¬¦åˆè¦æ±‚
    void change(char *array,int number);//å°†åŸå§‹æ•°æ®è½¬åŒ–æˆæ•°å€¼
    double change_to_aim(int tokind);//å°†åŸå§‹æ•°æ®è½¬åŒ–æˆç›®æ ‡è¿›åˆ¶çš„æ•°æ®
};

NumSysConversion::NumSysConversion()//æ„é€ å‡½æ•°
{
    stack=new Stack;
    queue=new Queue;
    inter=0;
    floater=0;
}

NumSysConversion::~NumSysConversion()//ææ„å‡½æ•°
{
    delete stack;
    delete queue;
}

bool NumSysConversion::check(char *array,int number)//æ£€æŸ¥è¾“å…¥æ•°æ®æ˜¯å¦ç¬¦åˆè¦æ±‚
{
    bool flag=true;
    for(int i=0;array[i]!='\0';i++)
    {
        cout<<array[i];
        if(array[i]-48>=number)
        {
            flag=false;
            break;
        }
    }
    return flag;
}

void NumSysConversion::change(char *array,int number)//å°†åŸå§‹æ•°æ®è½¬åŒ–æˆæ•°å€¼
{
    int flag=0,j=0;
    for(int i=0;array[i]!='\0';i++)
    {
        if(array[i]=='.')
        {
            flag=1;
            continue;
        }
        if(flag==0)//å–æ•´æ•°æ•°æ®
            inter=inter*number+array[i]-48;
        if(flag==1)//å–å°æ•°æ•°æ®
            floater+=(float)(((int)array[i]-48)*(float)pow(number,--j));
    }
}

double NumSysConversion::change_to_aim(int tokind)//å°†åŸå§‹æ•°æ®è½¬åŒ–æˆç›®æ ‡è¿›åˆ¶çš„æ•°æ®
{
    int count=0,temp,flag=0,num;
    double resnumb=0;
    //ç¬¬ä¸€æ­¥ï¼šå…ˆå°†æ•´æ•°éƒ¨åˆ†è½¬æ¢è¿›åˆ¶åçš„æ•°æ®ä¾æ¬¡å…¥æ ˆ
    while(inter)
    {
        num=inter%tokind;
        stack->push(num);
        inter/=tokind;
    }
    //ç¬¬äºŒæ­¥ï¼šå†å°†å°æ•°éƒ¨åˆ†è½¬æ¢è¿›åˆ¶åçš„æ•°æ®ä¾æ¬¡å…¥é˜Ÿ
    while(floater&&count<TAILMAXLENTH)
    {
        queue->append(int(floater*tokind));
        floater*=tokind;
        if(floater>=1)
            floater-=(int)floater;
        count++;
    }
    //ç¬¬ä¸‰æ­¥ï¼šæ˜¾ç¤ºæ ˆå’Œé˜Ÿä¸­çš„æ•°æ®å¹¶å°†å…¶è½¬æ¢æˆå¯ç”¨æ•°æ®
    cout<<"æ•°æ®è½¬æ¢åä¸ºï¼š";
    while(!stack->empty())
    {
        stack->top(temp);
        stack->pop();
        cout<<temp;
        resnumb=resnumb*tokind+temp;
        flag=1;
    }
    if(!queue->empty())
    {
        if(flag==0)
            cout<<"0";
        cout<<".";
        count=-1;
    }
    while(!queue->empty())
    {
        queue->retrieve(temp);
        queue->serve();
        cout<<temp;
        resnumb+=temp*pow(tokind,count--);
    }
    cout<<endl;
    //ç¬¬å››æ­¥ï¼šæ¸…ç©ºæ ˆå’Œé˜Ÿä¸­çš„æ•°æ®
    stack->clear();
    queue->clear();
    inter=0;
    floater=0;
    //ç¬¬äº”æ­¥ï¼šå°†å¯ç”¨æ•°æ®ä¿¡æ¯è¿”å›
    return resnumb;
}

