```
 #include&lt;stdio.h&gt;

int main()
{
	int n, i = 0;
	double x, max, aver, min, sum = 0;
	scanf("%d", &amp;n);
	while (i &lt; n){
		i += 1;
		scanf("%lf", &amp;x);
		if (i == 1) {		//在循环的第一次，用输入的值初始化max，min
			max = x;
			min = x;
		}
		else if (x &gt; max)
		{
			max = x;
		}
		else if (x &lt; min) {
			min = x;
		}
		sum += x;
	}
	aver = (sum - max - min)*0.1 / (n - 2) * 10;
	printf("%.2lf\n", aver);
	return 0;
}
