&lt;span class="hljs-keyword"&gt;using&lt;/span&gt; UnityEngine.UI;

&lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; 移动方向&lt;/span&gt;
&lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;/summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-built_in"&gt;enum&lt;/span&gt; MoveDirection
{
    X,
    OppositeX,
    Z,
    OppositeZ
}

&lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-keyword"&gt;sealed&lt;/span&gt; &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title"&gt;CreateMap&lt;/span&gt; : &lt;span class="hljs-title"&gt;MonoBehaviour&lt;/span&gt;
{
    [&lt;span class="hljs-meta"&gt;SerializeField&lt;/span&gt;] &lt;span class="hljs-keyword"&gt;private&lt;/span&gt; Vector3 startPoint;
    [&lt;span class="hljs-meta"&gt;SerializeField&lt;/span&gt;] &lt;span class="hljs-keyword"&gt;private&lt;/span&gt; Vector3 endPoint;
    [&lt;span class="hljs-meta"&gt;SerializeField&lt;/span&gt;] &lt;span class="hljs-keyword"&gt;private&lt;/span&gt; &lt;span class="hljs-built_in"&gt;float&lt;/span&gt; length;
    [&lt;span class="hljs-meta"&gt;SerializeField&lt;/span&gt;] &lt;span class="hljs-keyword"&gt;private&lt;/span&gt; &lt;span class="hljs-built_in"&gt;float&lt;/span&gt; width;
    [&lt;span class="hljs-meta"&gt;SerializeField&lt;/span&gt;] &lt;span class="hljs-keyword"&gt;private&lt;/span&gt; GameObject player;
    [&lt;span class="hljs-meta"&gt;SerializeField&lt;/span&gt;] &lt;span class="hljs-keyword"&gt;private&lt;/span&gt; InputField input;

    &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; 每步的米数&lt;/span&gt;
    &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;/summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;private&lt;/span&gt; &lt;span class="hljs-keyword"&gt;const&lt;/span&gt; &lt;span class="hljs-built_in"&gt;sbyte&lt;/span&gt; Step &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;

    &lt;span class="hljs-function"&gt;&lt;span class="hljs-keyword"&gt;private&lt;/span&gt; &lt;span class="hljs-keyword"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;Awake&lt;/span&gt;(&lt;span class="hljs-params"&gt;&lt;/span&gt;)&lt;/span&gt;
    {
        startPoint &amp;#61; player.transform.position;
        length &amp;#61; startPoint.z - endPoint.z;
        width &amp;#61; startPoint.x - endPoint.x;
        input.onEndEdit.AddListener((&lt;span class="hljs-keyword"&gt;value&lt;/span&gt;) &amp;#61;&amp;gt;
        {
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (!&lt;span class="hljs-built_in"&gt;sbyte&lt;/span&gt;.TryParse(&lt;span class="hljs-keyword"&gt;value&lt;/span&gt;, &lt;span class="hljs-keyword"&gt;out&lt;/span&gt; &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; result)) 
                &lt;span class="hljs-keyword"&gt;return&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;switch&lt;/span&gt; (result)
            {
                &lt;span class="hljs-keyword"&gt;case&lt;/span&gt; &lt;span class="hljs-number"&gt;1&lt;/span&gt;:
                    Move(result);
                    &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;
                &lt;span class="hljs-keyword"&gt;case&lt;/span&gt; &lt;span class="hljs-number"&gt;4&lt;/span&gt;:
                    Move(result);
                    &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;
                &lt;span class="hljs-literal"&gt;default&lt;/span&gt;:
                    Debug.LogError(&lt;span class="hljs-string"&gt;&amp;#34;输入错误&amp;#34;&lt;/span&gt;);
                    &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;
            }
        });
    }

    &lt;span class="hljs-function"&gt;&lt;span class="hljs-keyword"&gt;private&lt;/span&gt; &lt;span class="hljs-keyword"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;Move&lt;/span&gt;(&lt;span class="hljs-params"&gt;&lt;span class="hljs-built_in"&gt;sbyte&lt;/span&gt; step&lt;/span&gt;)&lt;/span&gt;
    {
        &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; playerPos &amp;#61; player.transform.position;
        &lt;span class="hljs-comment"&gt;//OppositeZ&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (Math.Abs(playerPos.x - startPoint.x) &amp;lt; &lt;span class="hljs-number"&gt;0.05f&lt;/span&gt;)
        {
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (playerPos.z &amp;#43; endPoint.z - step * Step &amp;gt;&amp;#61; -length)
            {
                playerPos.z -&amp;#61; step * Step;
            }
            &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
            {
                &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; (x, z) &amp;#61; CalculateMove(step, MoveDirection.OppositeZ, playerPos);
                playerPos.x &amp;#43;&amp;#61; x * Step;
                playerPos.z -&amp;#61; z * Step;
            }
        }
        &lt;span class="hljs-comment"&gt;//Z&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (Math.Abs(playerPos.x - endPoint.x) &amp;lt; &lt;span class="hljs-number"&gt;0.05f&lt;/span&gt;)
        {
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (playerPos.z &amp;#43; startPoint.z &amp;#43; step * Step &amp;lt;&amp;#61; length)
            {
                playerPos.z &amp;#43;&amp;#61; step * Step;
            }
            &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
            {
                &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; (x, z) &amp;#61; CalculateMove(step, MoveDirection.Z, playerPos);
                playerPos.x -&amp;#61; x * Step;
                playerPos.z &amp;#43;&amp;#61; z * Step;
            }
        }
        &lt;span class="hljs-comment"&gt;//OppositeX&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (Math.Abs(playerPos.z - startPoint.z) &amp;lt; &lt;span class="hljs-number"&gt;0.05f&lt;/span&gt;)
        {
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (playerPos.x &amp;#43; startPoint.x - step * Step &amp;gt;&amp;#61; -width)
            {
                playerPos.x -&amp;#61; step * Step;
            }
            &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
            {
                &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; (x, z) &amp;#61; CalculateMove(step, MoveDirection.OppositeX, playerPos);
                playerPos.x -&amp;#61; x * Step;
                playerPos.z -&amp;#61; z * Step;
            }
        }
        &lt;span class="hljs-comment"&gt;//X&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (Math.Abs(playerPos.z - endPoint.z) &amp;lt; &lt;span class="hljs-number"&gt;0.05f&lt;/span&gt;)
        {
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (playerPos.x &amp;#43; endPoint.x &amp;#43; step * Step &amp;lt;&amp;#61; width)
            {
                playerPos.x &amp;#43;&amp;#61; step * Step;
            }
            &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
            {
                &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; (x, z) &amp;#61; CalculateMove(step, MoveDirection.X, playerPos);
                playerPos.x &amp;#43;&amp;#61; x * Step;
                playerPos.z &amp;#43;&amp;#61; z * Step;
            }
        }

        player.transform.position &amp;#61; playerPos;
    }

    &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; 移动后超出界限重新计算&lt;/span&gt;
    &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;/summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;param name&amp;#61;&amp;#34;step&amp;#34;&amp;gt;&lt;/span&gt;&lt;span class="hljs-doctag"&gt;&amp;lt;/param&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;param name&amp;#61;&amp;#34;direction&amp;#34;&amp;gt;&lt;/span&gt;该移动的方向&lt;span class="hljs-doctag"&gt;&amp;lt;/param&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;param name&amp;#61;&amp;#34;playerPos&amp;#34;&amp;gt;&lt;/span&gt;&lt;span class="hljs-doctag"&gt;&amp;lt;/param&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;returns&amp;gt;&lt;/span&gt;返回x或z该移动的距离&lt;span class="hljs-doctag"&gt;&amp;lt;/returns&amp;gt;&lt;/span&gt;&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;private&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;float&lt;/span&gt; x, &lt;span class="hljs-built_in"&gt;float&lt;/span&gt; z) CalculateMove(&lt;span class="hljs-built_in"&gt;sbyte&lt;/span&gt; step, MoveDirection direction, Vector3 playerPos)
    {
        &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; stepToMove &amp;#61; step;
        &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (&lt;span class="hljs-literal"&gt;true&lt;/span&gt;)
        {
            &lt;span class="hljs-keyword"&gt;switch&lt;/span&gt; (direction)
            {
                &lt;span class="hljs-keyword"&gt;case&lt;/span&gt; MoveDirection.X:
                    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (!(step * Step &amp;#43; playerPos.x &amp;gt; endPoint.x)) &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; (step, stepToMove - step);
                    step--; &lt;span class="hljs-comment"&gt;//xStep&lt;/span&gt;
                    direction &amp;#61; MoveDirection.X;
                    &lt;span class="hljs-keyword"&gt;continue&lt;/span&gt;;
                &lt;span class="hljs-keyword"&gt;case&lt;/span&gt; MoveDirection.OppositeX:
                    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (!(-step * Step &amp;#43; playerPos.x &amp;lt; startPoint.x)) &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; (step, stepToMove - step);
                    step--; &lt;span class="hljs-comment"&gt;//xStep&lt;/span&gt;
                    direction &amp;#61; MoveDirection.OppositeX;
                    &lt;span class="hljs-keyword"&gt;continue&lt;/span&gt;;
                &lt;span class="hljs-keyword"&gt;case&lt;/span&gt; MoveDirection.Z:
                    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (!(step * Step &amp;#43; playerPos.z &amp;gt; startPoint.z)) &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; (stepToMove - step, step);
                    step--; &lt;span class="hljs-comment"&gt;//zStep&lt;/span&gt;
                    direction &amp;#61; MoveDirection.Z;
                    &lt;span class="hljs-keyword"&gt;continue&lt;/span&gt;;
                &lt;span class="hljs-keyword"&gt;case&lt;/span&gt; MoveDirection.OppositeZ:
                    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (!(-step * Step &amp;#43; playerPos.z &amp;lt; endPoint.z)) &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; (stepToMove - step, step);
                    step--; &lt;span class="hljs-comment"&gt;//zStep&lt;/span&gt;
                    direction &amp;#61; MoveDirection.OppositeZ;
                    &lt;span class="hljs-keyword"&gt;continue&lt;/span&gt;;
                &lt;span class="hljs-literal"&gt;default&lt;/span&gt;:
                    &lt;span class="hljs-keyword"&gt;throw&lt;/span&gt; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; ArgumentOutOfRangeException(&lt;span class="hljs-keyword"&gt;nameof&lt;/span&gt;(direction), direction, &lt;span class="hljs-literal"&gt;null&lt;/span&gt;);
            }
        }
    }
}
&lt;/code&gt;&lt;/pre&gt;
