        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    }
}
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; n,i,j;
    &lt;span class="hljs-built_in"&gt;srand&lt;/span&gt;((&lt;span class="hljs-keyword"&gt;unsigned&lt;/span&gt;)&lt;span class="hljs-built_in"&gt;time&lt;/span&gt;(&lt;span class="hljs-literal"&gt;NULL&lt;/span&gt;));
    i&amp;#61;&lt;span class="hljs-built_in"&gt;rand&lt;/span&gt;()%&lt;span class="hljs-number"&gt;100&lt;/span&gt;&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;&lt;span class="hljs-number"&gt;10&lt;/span&gt;;j&amp;#43;&amp;#43;){
