        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;n);
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;Guess&lt;/span&gt;(i,n)&amp;#61;&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;)
            &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;
    }
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(j&amp;gt;&amp;#61;&lt;span class="hljs-number"&gt;10&lt;/span&gt;)
