    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; 
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d次猜对\n&amp;#34;&lt;/span&gt;,j&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; 
}
