    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-number"&gt;4&lt;/span&gt;; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; &lt;span class="hljs-number"&gt;4&lt;/span&gt;; j&amp;#43;&amp;#43;)
            &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;, &amp;amp;x[i][j]);
    }
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-number"&gt;4&lt;/span&gt;; i&amp;#43;&amp;#43;) 
    {
        max &amp;#61; x[i][&lt;span class="hljs-number"&gt;0&lt;/span&gt;];
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; &lt;span class="hljs-number"&gt;4&lt;/span&gt;; j&amp;#43;&amp;#43;) &lt;span class="hljs-comment"&gt;//一行一行的算&lt;/span&gt;
        {
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (x[i][j] &amp;gt; max)
                {
                    max &amp;#61; x[i][j];
                    y &amp;#61; j; &lt;span class="hljs-comment"&gt;//求出每一行的最大值 然后在将其所在的列记录下来&lt;/span&gt;
                }
        }
        min &amp;#61; x[&lt;span class="hljs-number"&gt;0&lt;/span&gt;][y];
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (t &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; t &amp;lt; &lt;span class="hljs-number"&gt;4&lt;/span&gt;; t&amp;#43;&amp;#43;)
        {
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (x[t][y] &amp;lt; min) &lt;span class="hljs-comment"&gt;//用记录下来的y 去求那一列的最小值&lt;/span&gt;
            {
                min &amp;#61; x[t][y];
            }
        }
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (max &amp;#61;&amp;#61; min) &lt;span class="hljs-comment"&gt;//当最小值等于最大值时得到结果&lt;/span&gt;
        {
            &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d &amp;#34;&lt;/span&gt;, max);
        }
    }
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

