&lt;p&gt;代码如下&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; _CRT_SECURE_NO_WARNINGS&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;

&lt;span class="hljs-comment"&gt;//平均数&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;Mean&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; a[], &lt;span class="hljs-type"&gt;int&lt;/span&gt; n)&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; m &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; n; i&amp;#43;&amp;#43;)
        m &amp;#43;&amp;#61; a[i];
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; m / n;
}
&lt;span class="hljs-comment"&gt;//求中位数&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;Median&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; a[], &lt;span class="hljs-type"&gt;int&lt;/span&gt; n)&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-comment"&gt;//先排序&lt;/span&gt;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i, j, t;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; n - &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; n - &lt;span class="hljs-number"&gt;1&lt;/span&gt; - i; j&amp;#43;&amp;#43;)
        {
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (a[j] &amp;gt; a[j &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;])
            {
                t &amp;#61; a[j];
                a[j] &amp;#61; a[j &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;];
                a[j &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#61; t;
            }
        }
    }
    &lt;span class="hljs-comment"&gt;//如果是奇数个&amp;#xff0c;输出中间值&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (n % &lt;span class="hljs-number"&gt;2&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;)
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; a[n / &lt;span class="hljs-number"&gt;2&lt;/span&gt;];
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
    {
        &lt;span class="hljs-built_in"&gt;return&lt;/span&gt; (a[n / &lt;span class="hljs-number"&gt;2&lt;/span&gt;] &amp;#43; a[n / &lt;span class="hljs-number"&gt;2&lt;/span&gt; - &lt;span class="hljs-number"&gt;1&lt;/span&gt;]) / &lt;span class="hljs-number"&gt;2&lt;/span&gt;;
    }
}

&lt;span class="hljs-comment"&gt;//众数&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;Mode&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; a[], &lt;span class="hljs-type"&gt;int&lt;/span&gt; n)&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; cnt[&lt;span class="hljs-number"&gt;11&lt;/span&gt;] &amp;#61; { &lt;span class="hljs-number"&gt;0&lt;/span&gt; };&lt;span class="hljs-comment"&gt;//因为分数是从0-10&amp;#xff0c;所以用11个元素的数组记录每个数字出现的次数&lt;/span&gt;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i, max, index;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; n; i&amp;#43;&amp;#43;)
        cnt[a[i]] &amp;#43;&amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
    &lt;span class="hljs-comment"&gt;//找出最大数&lt;/span&gt;
    max &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
    index &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-number"&gt;11&lt;/span&gt;; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (cnt[i] &amp;gt; max)
        {
            max &amp;#61; cnt[i];
            index &amp;#61; i;
        }

    }
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; index;
}



&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; a[&lt;span class="hljs-number"&gt;40&lt;/span&gt;], mean &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;, median &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;, mode &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
