$array = Array(100, 200, 300, 400, 500);

echo getMax($array) . '&amp;lt;br/&amp;gt;';

$array = Array(1123212, 2143212, 8323212, 7223212, 1023212);

echo getMax($array) . '&amp;lt;br/&amp;gt;';
