&lt;p&gt;代码如下&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdlib.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; MAXRS 100&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; MAXKC 5&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;struct&lt;/span&gt; &lt;span class="hljs-title class_"&gt;Student&lt;/span&gt; 
{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; id;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; score[MAXKC];
};

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; n,m,k;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i,j;
    &lt;span class="hljs-type"&gt;float&lt;/span&gt; avg,mid;
    &lt;span class="hljs-keyword"&gt;struct&lt;/span&gt; &lt;span class="hljs-title class_"&gt;Student&lt;/span&gt; stu[MAXRS],tmp; 
    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d%d%d&amp;#34;&lt;/span&gt;,&amp;amp;n,&amp;amp;m,&amp;amp;k);
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;n;i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;stu[i].id);
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;m;j&amp;#43;&amp;#43;)
            &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;stu[i].score[j]);
    }
    &lt;span class="hljs-comment"&gt;//根据第k门成绩排序&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;n&lt;span class="hljs-number"&gt;-1&lt;/span&gt;;i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;n&lt;span class="hljs-number"&gt;-1&lt;/span&gt;-i;j&amp;#43;&amp;#43;)
        {
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(stu[j].score[k&lt;span class="hljs-number"&gt;-1&lt;/span&gt;] &amp;lt; stu[j&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;].score[k&lt;span class="hljs-number"&gt;-2&lt;/span&gt;])
            {
                tmp &amp;#61; stu[j];
                stu[j] &amp;#61; stu[j&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;];
                stu[j&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#61; tmp;
            }
        }
    }
    &lt;span class="hljs-comment"&gt;//计算平均成绩&lt;/span&gt;
    avg &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;n;i&amp;#43;&amp;#43;)
        avg &amp;#43;&amp;#61; stu[i].score[k&lt;span class="hljs-number"&gt;-1&lt;/span&gt;];
    avg &amp;#61; avg/n;
    &lt;span class="hljs-comment"&gt;//中位数&lt;/span&gt;
    mid &amp;#61; stu[(n&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;)/&lt;span class="hljs-number"&gt;2&lt;/span&gt;].score[k&lt;span class="hljs-number"&gt;-1&lt;/span&gt;];
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%.1f %d&amp;#34;&lt;/span&gt;,avg,mid);

    &lt;span class="hljs-comment"&gt;//根据学号排序&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;n&lt;span class="hljs-number"&gt;-1&lt;/span&gt;;i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;n&lt;span class="hljs-number"&gt;-1&lt;/span&gt;-i;j&amp;#43;&amp;#43;)
        {
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(stu[j].id &amp;gt; stu[j&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;].id)
            {
                tmp &amp;#61; stu[j];
                stu[j] &amp;#61; stu[j&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;];
                stu[j&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#61; tmp;
            }
        }
    }
    &lt;span class="hljs-comment"&gt;//输出所有第k门成绩为中位数的学生&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;n;i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(stu[i].score[k&lt;span class="hljs-number"&gt;-1&lt;/span&gt;] &amp;#61;&amp;#61; mid)
        {
            &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d &amp;#34;&lt;/span&gt;,stu[i].id);
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;m;j&amp;#43;&amp;#43;)
            {
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(j&amp;lt;m&lt;span class="hljs-number"&gt;-1&lt;/span&gt;)
                    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d &amp;#34;&lt;/span&gt;,stu[i].score[j]);
                &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
                    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d\n&amp;#34;&lt;/span&gt;,stu[i].score[j]);
            }
        }
    }
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-c&amp;#43;&amp;#43;"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&lt;span class="hljs-string"&gt;&amp;lt;bits/stdc&amp;#43;&amp;#43;.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
