&lt;p&gt;你题目的解答代码如下&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;long&lt;/span&gt; x;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; c&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;, max&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;,min&amp;#61;&lt;span class="hljs-number"&gt;9&lt;/span&gt;;
    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%ld&amp;#34;&lt;/span&gt;,&amp;amp;x);
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (x&amp;#61;&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;)
    {
        c&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        max&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;
        min&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    }
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (x&amp;gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;)
        {
            &lt;span class="hljs-type"&gt;int&lt;/span&gt; t &amp;#61; x%&lt;span class="hljs-number"&gt;10&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (t&amp;gt;max)
                max &amp;#61; t;
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (t&amp;lt;min)
                min &amp;#61; t;
            x/&amp;#61;&lt;span class="hljs-number"&gt;10&lt;/span&gt;;
            c&amp;#43;&amp;#43;;
        }
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d %d %d&amp;#34;&lt;/span&gt;,c,max,min);
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;
