#include &lt;stdio.h&gt;

int isprime(int x)
{
    if (x == 0 || x == 1) return 0;
    for (int i = 2; i &lt;= x / 2; i++)
        if (!(x % i)) return 0;
    return 1;
}

int isallprime(int x)
{
	do
    {
		if (!isprime(x)) return 0;
		x /= 10;
    }
	while (x &gt; 0);
    return 1;
}

int main()
{
    for (int i = 111; i &lt; 999; i++)
        if (isallprime(i)) printf("%d\n", i);
}
