&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; first&lt;span class="hljs-constructor"&gt;VEX(AMGraph G, &lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;u&lt;/span&gt;)&lt;/span&gt;;            &lt;span class="hljs-comment"&gt;//第一个邻接顶点        &lt;/span&gt;
&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; next&lt;span class="hljs-constructor"&gt;VEX(AMGraph G,&lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;w&lt;/span&gt; ,&lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;u&lt;/span&gt;)&lt;/span&gt;;    &lt;span class="hljs-comment"&gt;//下一个邻接顶点&lt;/span&gt;

