    }

    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;vexnum; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;vexnum; j&amp;#43;&amp;#43;)
        {
            &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;arr&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt; &amp;#61; MAXINT;        &lt;span class="hljs-comment"&gt;//邻接矩阵的初值都为无穷大&lt;/span&gt;
        }    
    }

    return &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
}
void show&lt;span class="hljs-constructor"&gt;Graph(AMGraph G)&lt;/span&gt;
{
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;vexnum; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;vexnum; j&amp;#43;&amp;#43;)
        {
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (&lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;arr&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt;&lt;span class="hljs-operator"&gt; &amp;#61;&amp;#61; &lt;/span&gt;MAXINT)    &lt;span class="hljs-comment"&gt;//无穷大弄得更好看点&lt;/span&gt;
                cout &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34;∞&amp;#34;&lt;/span&gt; &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34; &amp;#34;&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
                cout &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34; &amp;#34;&lt;/span&gt; &amp;lt;&amp;lt; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;arr&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt; &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34; &amp;#34;&lt;/span&gt;;
        }
