}

&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; locate&lt;span class="hljs-constructor"&gt;Vex(AMGraph G, &lt;span class="hljs-params"&gt;char&lt;/span&gt; &lt;span class="hljs-params"&gt;u&lt;/span&gt;)&lt;/span&gt;
{
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;vexnum; i&amp;#43;&amp;#43;)    
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (u&lt;span class="hljs-operator"&gt; &amp;#61;&amp;#61; &lt;/span&gt;&lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;vexs&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;)            &lt;span class="hljs-comment"&gt;//如果u的值和顶点数据匹配&amp;#xff0c;就返回顶点在矩阵中的下标&lt;/span&gt;
            return i;
    }
    return -&lt;span class="hljs-number"&gt;1&lt;/span&gt;;
}

&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; create&lt;span class="hljs-constructor"&gt;Graph(AMGraph&amp;amp; G)&lt;/span&gt;
{
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; w &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;            &lt;span class="hljs-comment"&gt;//i,j代表顶点下标,w代表权值&lt;/span&gt;
    &lt;span class="hljs-built_in"&gt;char&lt;/span&gt; v1 &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; &lt;span class="hljs-built_in"&gt;char&lt;/span&gt; v2 &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;            &lt;span class="hljs-comment"&gt;//v1,v2为顶点数据&lt;/span&gt;
    cout &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34;please input w of v1 to v2 in graph!&amp;#34;&lt;/span&gt; &amp;lt;&amp;lt; endl;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; k &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; k &amp;lt; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;arcnum; k&amp;#43;&amp;#43;)
    {
        cin &amp;gt;&amp;gt; v1 &amp;gt;&amp;gt; v2 &amp;gt;&amp;gt; w;            
        i &amp;#61; locate&lt;span class="hljs-constructor"&gt;Vex(G, &lt;span class="hljs-params"&gt;v1&lt;/span&gt;)&lt;/span&gt;;            &lt;span class="hljs-comment"&gt;//找到v1在顶点表的下标&amp;#xff0c;并返回赋值给i&lt;/span&gt;
        j &amp;#61; locate&lt;span class="hljs-constructor"&gt;Vex(G, &lt;span class="hljs-params"&gt;v2&lt;/span&gt;)&lt;/span&gt;;
        &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;arr&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt; &amp;#61; w;
        &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;arr&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt;&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt; &amp;#61; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;arr&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt;;        &lt;span class="hljs-comment"&gt;//无向图的矩阵是对称矩阵&lt;/span&gt;
    }

    cout &amp;lt;&amp;lt; endl;
    return &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
}

void dfs&lt;span class="hljs-constructor"&gt;AM(AMGraph G, &lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;i&lt;/span&gt;)&lt;/span&gt;
{&lt;!-- --&gt;&lt;span class="hljs-comment"&gt;//随机选一个顶点下标&amp;#xff0c;这里为0&lt;/span&gt;
    cout &amp;lt;&amp;lt; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;vexs&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;&amp;lt;&amp;lt;&lt;span class="hljs-string"&gt;&amp;#34; &amp;#34;&lt;/span&gt;;            &lt;span class="hljs-comment"&gt;//输出0下标在顶点表的值    &lt;/span&gt;
    visited&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt; &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;                &lt;span class="hljs-comment"&gt;//辅助数组对应下标i的值置为1&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;vexnum; j&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (&lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;arr&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt; !&amp;#61; MAXINT&lt;span class="hljs-operator"&gt; &amp;amp;&amp;amp; &lt;/span&gt;(!visited&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt;))    &lt;span class="hljs-comment"&gt;//只要是邻接的顶点并且没有访问过&lt;/span&gt;
        {                        &lt;span class="hljs-comment"&gt;//不然就退回&amp;#xff0c;也是递归结束条件&lt;/span&gt;
            dfs&lt;span class="hljs-constructor"&gt;AM(G, &lt;span class="hljs-params"&gt;j&lt;/span&gt;)&lt;/span&gt;;                &lt;span class="hljs-comment"&gt;//递归使用函数&lt;/span&gt;
        }
    }
}
&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; init&lt;span class="hljs-constructor"&gt;Queue(&lt;span class="hljs-params"&gt;sqQueue&lt;/span&gt;&amp;amp; Q)&lt;/span&gt;
{
    &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;base &amp;#61; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; *)malloc(sizeof(&lt;span class="hljs-built_in"&gt;int&lt;/span&gt;)&lt;span class="hljs-operator"&gt; * &lt;/span&gt;MVNUM);    
    &lt;span class="hljs-comment"&gt;//给base动态分配一个int*类型MVNUM个int大小的一维数组空间&lt;/span&gt;
    &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;front &amp;#61; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;rear &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;    &lt;span class="hljs-comment"&gt;//队头和对尾下标都置为0&lt;/span&gt;
    return &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
}
&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; en&lt;span class="hljs-constructor"&gt;Queue(&lt;span class="hljs-params"&gt;sqQueue&lt;/span&gt;&amp;amp; Q, &lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;i&lt;/span&gt;)&lt;/span&gt;
{
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; ((&lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;rear &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;) % MVNUM&lt;span class="hljs-operator"&gt; &amp;#61;&amp;#61; &lt;/span&gt;&lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;front)            &lt;span class="hljs-comment"&gt;//队满&lt;/span&gt;
        return &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;base&lt;span class="hljs-literal"&gt;[Q.&lt;span class="hljs-identifier"&gt;rear&lt;/span&gt;]&lt;/span&gt; &amp;#61; i;                    &lt;span class="hljs-comment"&gt;//先赋值再加&lt;/span&gt;
    &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;rear &amp;#61; (&lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;rear &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;) % MVNUM;
    return &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
}

&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; de&lt;span class="hljs-constructor"&gt;Queue(&lt;span class="hljs-params"&gt;sqQueue&lt;/span&gt;&amp;amp; Q, &lt;span class="hljs-params"&gt;int&lt;/span&gt; &amp;amp;&lt;span class="hljs-params"&gt;u&lt;/span&gt;)&lt;/span&gt;
{
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (&lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;rear&lt;span class="hljs-operator"&gt; &amp;#61;&amp;#61; &lt;/span&gt;&lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;front)                    &lt;span class="hljs-comment"&gt;//队空&lt;/span&gt;
        return &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    u &amp;#61; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;base&lt;span class="hljs-literal"&gt;[Q.&lt;span class="hljs-identifier"&gt;front&lt;/span&gt;]&lt;/span&gt;;                    &lt;span class="hljs-comment"&gt;//要删的值给u然后再加&lt;/span&gt;
    &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;front &amp;#61; (&lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;front &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;) % MVNUM;
    return &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
}

&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; first&lt;span class="hljs-constructor"&gt;VEX(AMGraph G, &lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;u&lt;/span&gt;)&lt;/span&gt;
{&lt;!-- --&gt;&lt;span class="hljs-comment"&gt;//在邻接矩阵u行0列开始遍历&amp;#xff0c;如果找到不等于无穷的&amp;#xff0c;&lt;/span&gt;
&lt;span class="hljs-comment"&gt;//并且没有访问过的就返回列的下标&amp;#xff0c;否则就返回无穷&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;vexnum; i&amp;#43;&amp;#43;)        
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (&lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;arr&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;u&lt;/span&gt;]&lt;/span&gt;&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt; !&amp;#61; MAXINT&lt;span class="hljs-operator"&gt; &amp;amp;&amp;amp; &lt;/span&gt;visit&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;&lt;span class="hljs-operator"&gt; &amp;#61;&amp;#61; &lt;/span&gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;) 
        {
            return i;
        }
    }
    return MAXINT;
}

&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; next&lt;span class="hljs-constructor"&gt;VEX(AMGraph G, &lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;w&lt;/span&gt;, &lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;u&lt;/span&gt;)&lt;/span&gt;
{&lt;!-- --&gt;&lt;span class="hljs-comment"&gt;//在邻接矩阵u行w&amp;#43;1列开始遍历&amp;#xff0c;如果找到不等于无穷的&amp;#xff0c;&lt;/span&gt;
&lt;span class="hljs-comment"&gt;//并且没有访问过的就返回列的下标&amp;#xff0c;否则就返回无穷&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i &amp;#61; w &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;vexnum; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (&lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;arr&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;u&lt;/span&gt;]&lt;/span&gt;&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt; !&amp;#61; MAXINT&lt;span class="hljs-operator"&gt; &amp;amp;&amp;amp; &lt;/span&gt;visit&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;&lt;span class="hljs-operator"&gt; &amp;#61;&amp;#61; &lt;/span&gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;)
        {
            return i;
        }    
    }
    return MAXINT;
}

void bfs&lt;span class="hljs-constructor"&gt;AM(AMGraph G, &lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;i&lt;/span&gt;)&lt;/span&gt;
{&lt;!-- --&gt;&lt;span class="hljs-comment"&gt;//随机选一个顶点下标&amp;#xff0c;这里为0&lt;/span&gt;
    cout &amp;lt;&amp;lt; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;vexs&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt; &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34; &amp;#34;&lt;/span&gt;;        &lt;span class="hljs-comment"&gt;//输出i下标在顶点表的值    &lt;/span&gt;
    visit&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt; &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;                &lt;span class="hljs-comment"&gt;//辅助数值对应下标i的值置为1&lt;/span&gt;
    sqQueue Q;                    
    init&lt;span class="hljs-constructor"&gt;Queue(Q)&lt;/span&gt;;
    en&lt;span class="hljs-constructor"&gt;Queue(Q, &lt;span class="hljs-params"&gt;i&lt;/span&gt;)&lt;/span&gt;;                &lt;span class="hljs-comment"&gt;//i为矩阵中顶点的行下标&amp;#xff0c;让它入队(顶点表的下标和矩阵的列下标&amp;#xff0c;行下标一致&amp;#xff0c;本算法中说谁的下标都一样)&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (&lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;rear !&amp;#61; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;Q&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;front)        &lt;span class="hljs-comment"&gt;//队不为空&lt;/span&gt;
    {
        &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; u &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;            &lt;span class="hljs-comment"&gt;//接收矩阵中顶点的行下标&amp;#xff0c;因为是邻接矩阵&lt;/span&gt;
        de&lt;span class="hljs-constructor"&gt;Queue(Q,&lt;span class="hljs-params"&gt;u&lt;/span&gt;)&lt;/span&gt;;            &lt;span class="hljs-comment"&gt;//出队并让u接收矩阵中顶点的行下标&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; w &amp;#61; first&lt;span class="hljs-constructor"&gt;VEX(G, &lt;span class="hljs-params"&gt;u&lt;/span&gt;)&lt;/span&gt;; w !&amp;#61; MAXINT; w &amp;#61; next&lt;span class="hljs-constructor"&gt;VEX(G, &lt;span class="hljs-params"&gt;w&lt;/span&gt;, &lt;span class="hljs-params"&gt;u&lt;/span&gt;)&lt;/span&gt;)
        {&lt;!-- --&gt;&lt;span class="hljs-comment"&gt;//注意在一次循环中u不变&lt;/span&gt;
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (!visit&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;w&lt;/span&gt;]&lt;/span&gt;)
            {
                cout &amp;lt;&amp;lt; &lt;span class="hljs-module-access"&gt;&lt;span class="hljs-module"&gt;&lt;span class="hljs-identifier"&gt;G&lt;/span&gt;.&lt;/span&gt;&lt;/span&gt;vexs&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;w&lt;/span&gt;]&lt;/span&gt; &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34; &amp;#34;&lt;/span&gt;;
                visit&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;w&lt;/span&gt;]&lt;/span&gt; &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
                en&lt;span class="hljs-constructor"&gt;Queue(Q, &lt;span class="hljs-params"&gt;w&lt;/span&gt;)&lt;/span&gt;;
            }
        }
    }

}
