bool arriveEnd(node src, node dest) {
     for(every node i pointed by src) {
          if(i == dest) {
              push(i) to stack;
              return true;
          } else if(arriveEnd(i, dest)) { 
             push(i) to stack;
             return true;
          }
          return false;
    }
}
void print  () {
    if(arriveEnd(src,dest))
       print stack.pop();
}

