&lt;pre&gt;&lt;code class="language-javascript"&gt;import &lt;span class="hljs-built_in"&gt;os&lt;/span&gt;

class Mnist:
    def __init__(&lt;span class="hljs-built_in"&gt;self&lt;/span&gt;,&lt;span class="hljs-built_in"&gt;path&lt;/span&gt;):
        &lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.&lt;span class="hljs-built_in"&gt;path&lt;/span&gt;&amp;#61;&lt;span class="hljs-built_in"&gt;path&lt;/span&gt;
    def __len__(&lt;span class="hljs-built_in"&gt;self&lt;/span&gt;):
        count&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; root,dirs,files &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; &lt;span class="hljs-built_in"&gt;os&lt;/span&gt;.walk(&lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.&lt;span class="hljs-built_in"&gt;path&lt;/span&gt;):
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; file &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; files:
                ext&amp;#61;&lt;span class="hljs-built_in"&gt;os&lt;/span&gt;.&lt;span class="hljs-built_in"&gt;path&lt;/span&gt;.splitext(file)[&lt;span class="hljs-number"&gt;-1&lt;/span&gt;].&lt;span class="hljs-built_in"&gt;lower&lt;/span&gt;()
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; ext&amp;#61;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;.jpg&amp;#39;&lt;/span&gt;:
                    count&amp;#43;&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; count
    def __getitem__(&lt;span class="hljs-built_in"&gt;self&lt;/span&gt;):
        li&amp;#61;[]
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; root,dirs,files &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; &lt;span class="hljs-built_in"&gt;os&lt;/span&gt;.walk(&lt;span class="hljs-built_in"&gt;self&lt;/span&gt;.&lt;span class="hljs-built_in"&gt;path&lt;/span&gt;):
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; d &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; dirs:
                &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; f &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; &lt;span class="hljs-built_in"&gt;os&lt;/span&gt;.listdir(&lt;span class="hljs-built_in"&gt;os&lt;/span&gt;.&lt;span class="hljs-built_in"&gt;path&lt;/span&gt;.join(root,d)):
                    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; f.endswith(&lt;span class="hljs-string"&gt;&amp;#39;.jpg&amp;#39;&lt;/span&gt;):
                        li.append(&lt;span class="hljs-built_in"&gt;os&lt;/span&gt;.&lt;span class="hljs-built_in"&gt;path&lt;/span&gt;.join(d,f).rsplit(&lt;span class="hljs-string"&gt;&amp;#39;\\&amp;#39;&lt;/span&gt;)[&lt;span class="hljs-number"&gt;1&lt;/span&gt;])
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; li
p&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;imgs&amp;#39;&lt;/span&gt;
M&amp;#61;Mnist(p)
&lt;span class="hljs-built_in"&gt;print&lt;/span&gt;(M.__len__())
&lt;span class="hljs-built_in"&gt;print&lt;/span&gt;(M.__getitem__())
