//你用邻接矩阵存储，我的使用邻接表...

#include &lt;stdio.h&gt;  
#include &lt;malloc.h&gt;  
#define VERTEXNUM 5  
  
//存放顶点的邻接表元素  
typedef struct edge{  
        int vertex;  
        struct edge* next;  
}st_edge;  
  
//队列的元素  
typedef struct qElement{  
        int value;  
        struct qElement* pre;  
        struct qElement* next;  
}st_qElement;  
  
//队列的前端和后端，最后一个入队列的元素为rear，第一个出队列的元素为front  
st_qElement* front = NULL;  
st_qElement* rear = NULL;  
  
void createGraph(st_edge** edge, int start, int end);  
void displayGraph(st_edge** edge);  
void delGraph(st_edge** edge);  
void BFT(st_edge** edge,int* vertexStatusArr);  
void BFTcore(st_edge** edge,int i,int* vertexStatusArr);  
void putQueue(int vertex);  
int* getQueue();  
void putRelatedInQueue(st_edge** edge, int vertex);  
  
int main(void){  
        //动态创建存放边的指针数组   
        st_edge** edge = (st_edge**)malloc(sizeof(st_edge*)*VERTEXNUM);  
        int i;  
        for(i=0;i&lt;VERTEXNUM;i++){  
                edge[i] = NULL;  
        }  
          
        //存放顶点的遍历状态，0：未遍历，1：已遍历  
        int* vertexStatusArr = (int*)malloc(sizeof(int)*VERTEXNUM);  
        for(i=0;i&lt;VERTEXNUM;i++){  
                vertexStatusArr[i] = 0;  
        }  
  
        printf("after init:\n");  
        displayGraph(edge);  
        //创建图  
        createGraph(edge,0,3);  
        createGraph(edge,0,4);  
        createGraph(edge,3,1);  
        createGraph(edge,3,2);  
        createGraph(edge,4,1);  
  
        printf("after create:\n");  
        displayGraph(edge);  
          
        //广度优先遍历  
        BFT(edge,vertexStatusArr);  
          
        //释放邻接表占用的内存  
        delGraph(edge);  
        edge = NULL;  
        free(vertexStatusArr);  
        vertexStatusArr = NULL;  
        return 0;  
}  
  
//创建图  
void createGraph(st_edge** edge, int start, int end){  
        st_edge* newedge = (st_edge*)malloc(sizeof(st_edge));  
        newedge-&gt;vertex = end;  
        newedge-&gt;next = NULL;  
        edge = edge + start;  
        while(*edge != NULL){  
                edge = &amp;((*edge)-&gt;next);  
        }  
        *edge = newedge;  
}  
  
//打印存储的图  
void displayGraph(st_edge** edge){  
        int i;  
        st_edge* p;  
        for(i=0;i&lt;VERTEXNUM;i++){  
                printf("%d:",i);  
                p = *(edge+i);  
                while(p != NULL){  
                        printf("%d ",p-&gt;vertex);  
                        p = p-&gt;next;  
                }  
                printf("\n");  
        }  
}  
  
//释放邻接表占用的内存  
void delGraph(st_edge** edge){  
        int i;  
        st_edge* p;  
        st_edge* del;  
        for(i=0;i&lt;VERTEXNUM;i++){  
                p = *(edge+i);  
                while(p != NULL){  
                        del = p;  
                        p = p-&gt;next;  
                        free(del);  
                }  
                edge[i] = NULL;  
        }  
        free(edge);  
}  
  
//广度优先遍历  
void BFT(st_edge** edge,int* vertexStatusArr){  
        printf("start BFT graph:\n");  
        int i;  
        for(i=0;i&lt;VERTEXNUM;i++){  
                BFTcore(edge,i,vertexStatusArr);  
        }  
        printf("\n");  
}  
  
void BFTcore(st_edge** edge,int i,int* vertexStatusArr){  
        putQueue(i);  
        int* qeValue = NULL;  
        while((qeValue = getQueue()) != NULL){  
                if(vertexStatusArr[*qeValue] == 0){  
                        printf("%d ",*qeValue);  
                        vertexStatusArr[*qeValue] = 1;  
                        putRelatedInQueue(edge, *qeValue);  
                }  
                free(qeValue);  
                qeValue = NULL;  
        }  
}  
  
//将元素放到队列中  
void putQueue(int vertex){  
        st_qElement* qe = (st_qElement*)malloc(sizeof(st_qElement));  
        qe-&gt;value = vertex;  
        qe-&gt;next = NULL;  
        qe-&gt;pre = NULL;  
        if(front == NULL || rear == NULL){  
                front = rear = qe;  
        }else{  
                rear-&gt;next = qe;  
                qe-&gt;pre = rear;  
                rear = qe;  
        }  
}  
  
//从队列中获取一个元素  
int* getQueue(){  
        if(front == NULL || rear == NULL){  
                return NULL;  
        }else{  
                int* res = (int*)malloc(sizeof(int));  
                *res = front-&gt;value;  
  
                st_qElement* p = front;  
                front = front-&gt;next;  
                if(front == NULL){  
                        rear = front;  
                }else{  
                        front-&gt;pre = NULL;  
                }  
                free(p);  
                p = NULL;  
                return res;  
        }  
}  
  
//将顶点关联的元素放到队列中  
void putRelatedInQueue(st_edge** edge, int vertex){  
        st_edge* p = *(edge+vertex);  
        while(p != NULL){  
                putQueue(p-&gt;vertex);  
                p = p-&gt;next;  
        }  
}

