      &lt;span class="md_link_desc" style=""&gt;BFS/DFS算法解析【算法入门]2018/6/21.前言和树的遍历类似&amp;#xff0c;图的遍历也是从图中某点出发&amp;#xff0c;然后按照某种方法对图中所有顶点进行访问&amp;#xff0c;且仅访问一次。但是图的遍历相对树而言要更为复杂。因为图中的任意顶点都可能与其他顶点相邻&amp;#xff0c;所以在图的遍历中必须记录已被访问的顶点&amp;#xff0c;避免重复访问。根据搜索路径的不同&amp;#xff0c;我们可以将遍历图的方法分为两种&amp;#xff1a;广度优先搜索和深度优先搜索。2.图的基本概念2.1.无向图和无向...&lt;/span&gt;
      &lt;span class="md_flex_card"&gt;
      &lt;img class="md_link_img" id="md_link_img" src="https://g.csdnimg.cn/static/logo/favicon32.ico" /&gt;
        &lt;span class="flex-1"&gt;
          &lt;span class="md_link_url"&gt;https://blog.csdn.net/weixin_40953222/article/details/80544928?spm&amp;#61;1001.2101.3001.6650.3&amp;amp;utm_medium&amp;#61;distribute.pc_relevant.none-task-blog-2%7Edefault%7ECTRLIST%7Edefault-3.no_search_link&amp;amp;depth_1-utm_source&amp;#61;distribute.pc_relevant.none-task-blog-2%7Edefault%7ECTRLIST%7Edefault-3.no_search_link&lt;/span&gt;
        &lt;/span&gt;
      &lt;/span&gt;&lt;/div&gt;&lt;/a&gt;&lt;br /&gt;广度优先&amp;#xff1a;&lt;br /&gt;&lt;span style="display:inline-block;text-indent:2em;"&gt;     第一层-第二层&amp;#xff1a;1)A-D 2)A-E 3)A-B&lt;/span&gt;&lt;br /&gt;&lt;span style="display:inline-block;text-indent:2em;"&gt;     第二层-第三层&amp;#xff1a;4)D-G  5)B-C&lt;/span&gt;&lt;br /&gt;&lt;span style="display:inline-block;text-indent:2em;"&gt;     第三层-第四层&amp;#xff1a;6)G-H 7)C-F&lt;/span&gt;&lt;br /&gt;&lt;span style="display:inline-block;text-indent:2em;"&gt;     第四层-第五层&amp;#xff1a;8&amp;#xff09;H-I&lt;/span&gt;&lt;br /&gt;深度优先&amp;#xff1a;&lt;br /&gt;&lt;span style="display:inline-block;text-indent:2em;"&gt;    1&amp;#xff09;A-D   2&amp;#xff09;D-B   3&amp;#xff09;B-C   4&amp;#xff09;C-F&lt;/span&gt;&lt;br /&gt;&lt;span style="display:inline-block;text-indent:2em;"&gt;    5&amp;#xff09;(回溯)F-C 6&amp;#xff09;(回溯)C-B&lt;/span&gt;&lt;br /&gt;&lt;span style="display:inline-block;text-indent:2em;"&gt;    7&amp;#xff09;B-D   8&amp;#xff09;D-G  9&amp;#xff09;G-E&lt;/span&gt;&lt;br /&gt;&lt;span style="display:inline-block;text-indent:2em;"&gt;    10&amp;#xff09;(回溯)E-G&lt;/span&gt;&lt;br /&gt;&lt;span style="display:inline-block;text-indent:2em;"&gt;    11&amp;#xff09;G-H  12&amp;#xff09;H-I&lt;/span&gt;&lt;br /&gt;&lt;span style="display:inline-block;text-indent:2em;"&gt;    13&amp;#xff09;(回溯)I-H  14&amp;#xff09;(回溯)H-G  15&amp;#xff09;(回溯)G-D 16&amp;#xff09;(回溯)D-A&lt;/span&gt;&lt;/p&gt;

&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-keyword"&gt;import&lt;/span&gt; java.util.*;

&lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title class_"&gt;MazeBFS&lt;/span&gt; {
    &lt;span class="hljs-comment"&gt;// 迷宫地图&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;private&lt;/span&gt; &lt;span class="hljs-type"&gt;char&lt;/span&gt;[][] maze;
    &lt;span class="hljs-comment"&gt;// 迷宫大小&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;private&lt;/span&gt; &lt;span class="hljs-type"&gt;int&lt;/span&gt; M, N;
    &lt;span class="hljs-comment"&gt;// 上下左右四个方向&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;private&lt;/span&gt; &lt;span class="hljs-type"&gt;int&lt;/span&gt;[][] directions &amp;#61; {&lt;!-- --&gt;{&lt;!-- --&gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;, &lt;span class="hljs-number"&gt;1&lt;/span&gt;}, {&lt;!-- --&gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;, -&lt;span class="hljs-number"&gt;1&lt;/span&gt;}, {&lt;!-- --&gt;&lt;span class="hljs-number"&gt;1&lt;/span&gt;, &lt;span class="hljs-number"&gt;0&lt;/span&gt;}, {-&lt;span class="hljs-number"&gt;1&lt;/span&gt;, &lt;span class="hljs-number"&gt;0&lt;/span&gt;}};
    &lt;span class="hljs-comment"&gt;// 记录已访问的位置&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;private&lt;/span&gt; &lt;span class="hljs-type"&gt;boolean&lt;/span&gt;[][] visited;

    &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-title function_"&gt;MazeBFS&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;char&lt;/span&gt;[][] maze)&lt;/span&gt; {
        &lt;span class="hljs-built_in"&gt;this&lt;/span&gt;.maze &amp;#61; maze;
        &lt;span class="hljs-built_in"&gt;this&lt;/span&gt;.M &amp;#61; maze.length;
        &lt;span class="hljs-built_in"&gt;this&lt;/span&gt;.N &amp;#61; maze[&lt;span class="hljs-number"&gt;0&lt;/span&gt;].length;
        &lt;span class="hljs-built_in"&gt;this&lt;/span&gt;.visited &amp;#61; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-title class_"&gt;boolean&lt;/span&gt;[M][N];
    }

    &lt;span class="hljs-comment"&gt;// 广度优先搜索&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; List&amp;lt;&lt;span class="hljs-type"&gt;int&lt;/span&gt;[]&amp;gt; bfs(&lt;span class="hljs-type"&gt;int&lt;/span&gt; start_x, &lt;span class="hljs-type"&gt;int&lt;/span&gt; start_y, &lt;span class="hljs-type"&gt;int&lt;/span&gt; end_x, &lt;span class="hljs-type"&gt;int&lt;/span&gt; end_y) {
        Queue&amp;lt;&lt;span class="hljs-type"&gt;int&lt;/span&gt;[]&amp;gt; queue &amp;#61; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-title class_"&gt;LinkedList&lt;/span&gt;&amp;lt;&amp;gt;();
        queue.offer(&lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-title class_"&gt;int&lt;/span&gt;[]{start_x, start_y});
        visited[start_x][start_y] &amp;#61; &lt;span class="hljs-literal"&gt;true&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (!queue.isEmpty()) {
            &lt;span class="hljs-type"&gt;int&lt;/span&gt;[] pos &amp;#61; queue.poll();
            &lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-variable"&gt;x&lt;/span&gt; &lt;span class="hljs-operator"&gt;&amp;#61;&lt;/span&gt; pos[&lt;span class="hljs-number"&gt;0&lt;/span&gt;], y &amp;#61; pos[&lt;span class="hljs-number"&gt;1&lt;/span&gt;];
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt;[] dir : directions) {
                &lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-variable"&gt;nx&lt;/span&gt; &lt;span class="hljs-operator"&gt;&amp;#61;&lt;/span&gt; x &amp;#43; dir[&lt;span class="hljs-number"&gt;0&lt;/span&gt;], ny &amp;#61; y &amp;#43; dir[&lt;span class="hljs-number"&gt;1&lt;/span&gt;];
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (nx &amp;gt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; &amp;amp;&amp;amp; nx &amp;lt; M &amp;amp;&amp;amp; ny &amp;gt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; &amp;amp;&amp;amp; ny &amp;lt; N &amp;amp;&amp;amp; maze[nx][ny] &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt; &amp;amp;&amp;amp; !visited[nx][ny]) {
                    visited[nx][ny] &amp;#61; &lt;span class="hljs-literal"&gt;true&lt;/span&gt;;
                    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (nx &amp;#61;&amp;#61; end_x &amp;amp;&amp;amp; ny &amp;#61;&amp;#61; end_y) {
                        List&amp;lt;&lt;span class="hljs-type"&gt;int&lt;/span&gt;[]&amp;gt; path &amp;#61; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-title class_"&gt;ArrayList&lt;/span&gt;&amp;lt;&amp;gt;();
                        path.add(&lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-title class_"&gt;int&lt;/span&gt;[]{nx, ny});
                        &lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-variable"&gt;cur_x&lt;/span&gt; &lt;span class="hljs-operator"&gt;&amp;#61;&lt;/span&gt; x, cur_y &amp;#61; y;
                        &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (cur_x !&amp;#61; start_x || cur_y !&amp;#61; start_y) {
                            path.add(&lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-title class_"&gt;int&lt;/span&gt;[]{cur_x, cur_y});
                            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt;[] dir2 : directions) {
                                &lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-variable"&gt;prev_x&lt;/span&gt; &lt;span class="hljs-operator"&gt;&amp;#61;&lt;/span&gt; cur_x - dir2[&lt;span class="hljs-number"&gt;0&lt;/span&gt;], prev_y &amp;#61; cur_y - dir2[&lt;span class="hljs-number"&gt;1&lt;/span&gt;];
                                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (prev_x &amp;gt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; &amp;amp;&amp;amp; prev_x &amp;lt; M &amp;amp;&amp;amp; prev_y &amp;gt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; &amp;amp;&amp;amp; prev_y &amp;lt; N &amp;amp;&amp;amp; visited[prev_x][prev_y]) {
                                    cur_x &amp;#61; prev_x;
                                    cur_y &amp;#61; prev_y;
                                    &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;
                                }
                            }
                        }
                        path.add(&lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-title class_"&gt;int&lt;/span&gt;[]{start_x, start_y});
                        Collections.reverse(path);
                        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; path;
                    }
                    queue.offer(&lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-title class_"&gt;int&lt;/span&gt;[]{nx, ny});
                }
            }
        }
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-literal"&gt;null&lt;/span&gt;;
    }

    &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-keyword"&gt;static&lt;/span&gt; &lt;span class="hljs-keyword"&gt;void&lt;/span&gt; &lt;span class="hljs-title function_"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;(String[] args)&lt;/span&gt; {
        &lt;span class="hljs-comment"&gt;// 迷宫地图&lt;/span&gt;
        &lt;span class="hljs-type"&gt;char&lt;/span&gt;[][] maze &amp;#61; {
                {&lt;!-- --&gt;&lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;},
                {&lt;!-- --&gt;&lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;},
                {&lt;!-- --&gt;&lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;},
                {&lt;!-- --&gt;&lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;},
                {&lt;!-- --&gt;&lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;},
                {&lt;!-- --&gt;&lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;},
                {&lt;!-- --&gt;&lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;X&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;O&amp;#39;&lt;/span&gt;},
        };

        &lt;span class="hljs-type"&gt;MazeBFS&lt;/span&gt; &lt;span class="hljs-variable"&gt;solution&lt;/span&gt; &lt;span class="hljs-operator"&gt;&amp;#61;&lt;/span&gt; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-title class_"&gt;MazeBFS&lt;/span&gt;(maze);
        List&amp;lt;&lt;span class="hljs-type"&gt;int&lt;/span&gt;[]&amp;gt; shortestPath&amp;#61; solution.bfs(&lt;span class="hljs-number"&gt;0&lt;/span&gt;, &lt;span class="hljs-number"&gt;0&lt;/span&gt;, maze.length - &lt;span class="hljs-number"&gt;1&lt;/span&gt;, maze[&lt;span class="hljs-number"&gt;0&lt;/span&gt;].length - &lt;span class="hljs-number"&gt;1&lt;/span&gt;);
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (shortestPath &amp;#61;&amp;#61; &lt;span class="hljs-literal"&gt;null&lt;/span&gt;) {
            System.out.println(&lt;span class="hljs-string"&gt;&amp;#34;无法到达终点&amp;#xff01;&amp;#34;&lt;/span&gt;);
        } &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; {
            System.out.println(&lt;span class="hljs-string"&gt;&amp;#34;最短路径为&amp;#xff1a;&amp;#34;&lt;/span&gt;);
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt;[] pos : shortestPath) {
                System.out.println(pos[&lt;span class="hljs-number"&gt;0&lt;/span&gt;] &amp;#43; &lt;span class="hljs-string"&gt;&amp;#34; &amp;#34;&lt;/span&gt; &amp;#43; pos[&lt;span class="hljs-number"&gt;1&lt;/span&gt;]);
            }
        }
    }
}
