}

void bfs( int x)
{
    enqueue(x, 0);
    vis[x] = 1;

    while(head<tail)
    {
        int i, nowx, nowtime;
        nowx = que[head][0];
        nowtime = que[head][1];
        head ++;

        if( nowx == k)
        {
            ans = nowtime;
            break;
        }

        for( i=1; i<=3; i++)
        {
            if( i==1 && vis[nowx-1]!=1 && nowx-1>=0 && nowx-1 <= MAX_N)
            {
                enqueue( nowx-1, nowtime+1);
                vis[nowx-1] = 1;

            }
            if( i==2 && vis[nowx+1]!=1 && nowx+1>=0 && nowx+1 <= MAX_N)
            {
                enqueue( nowx+1, nowtime+1);
                vis[nowx+1] = 1;
            }
            if( i==3 && vis[nowx*2]!=1 && nowx*2>=0 && nowx*2 <= MAX_N)
            {
                enqueue( nowx*2, nowtime+1);
                vis[nowx*2] = 1;
            }
        }
    }
}

void enqueue( int x, int time)
{
    que[tail][0] = x;
    que[tail][1] = time;
