                    neighbors.&lt;span class="hljs-keyword"&gt;add&lt;/span&gt;(r * nc &amp;#43; c);
                    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (!neighbors.isEmpty()) {
                        &lt;span class="hljs-type"&gt;int&lt;/span&gt; id &amp;#61; neighbors.remove();
                        &lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-keyword"&gt;row&lt;/span&gt; &amp;#61; id / nc;
                        &lt;span class="hljs-type"&gt;int&lt;/span&gt; col &amp;#61; id % nc;
                        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (&lt;span class="hljs-keyword"&gt;row&lt;/span&gt; - &lt;span class="hljs-number"&gt;1&lt;/span&gt; &amp;gt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; &amp;amp;&amp;amp; grid[&lt;span class="hljs-keyword"&gt;row&lt;/span&gt;&lt;span class="hljs-number"&gt;-1&lt;/span&gt;][col] &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;) {
                            neighbors.&lt;span class="hljs-keyword"&gt;add&lt;/span&gt;((&lt;span class="hljs-keyword"&gt;row&lt;/span&gt;&lt;span class="hljs-number"&gt;-1&lt;/span&gt;) * nc &amp;#43; col);
                            grid[&lt;span class="hljs-keyword"&gt;row&lt;/span&gt;&lt;span class="hljs-number"&gt;-1&lt;/span&gt;][col] &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
                        }
                        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (&lt;span class="hljs-keyword"&gt;row&lt;/span&gt; &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt; &amp;lt; nr &amp;amp;&amp;amp; grid[&lt;span class="hljs-keyword"&gt;row&lt;/span&gt;&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;][col] &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;) {
                            neighbors.&lt;span class="hljs-keyword"&gt;add&lt;/span&gt;((&lt;span class="hljs-keyword"&gt;row&lt;/span&gt;&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;) * nc &amp;#43; col);
                            grid[&lt;span class="hljs-keyword"&gt;row&lt;/span&gt;&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;][col] &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
                        }
                        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (col - &lt;span class="hljs-number"&gt;1&lt;/span&gt; &amp;gt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; &amp;amp;&amp;amp; grid[&lt;span class="hljs-keyword"&gt;row&lt;/span&gt;][col&lt;span class="hljs-number"&gt;-1&lt;/span&gt;] &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;) {
                            neighbors.&lt;span class="hljs-keyword"&gt;add&lt;/span&gt;(&lt;span class="hljs-keyword"&gt;row&lt;/span&gt; * nc &amp;#43; col&lt;span class="hljs-number"&gt;-1&lt;/span&gt;);
                            grid[&lt;span class="hljs-keyword"&gt;row&lt;/span&gt;][col&lt;span class="hljs-number"&gt;-1&lt;/span&gt;] &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
                        }
                        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (col &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt; &amp;lt; nc &amp;amp;&amp;amp; grid[&lt;span class="hljs-keyword"&gt;row&lt;/span&gt;][col&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;) {
                            neighbors.&lt;span class="hljs-keyword"&gt;add&lt;/span&gt;(&lt;span class="hljs-keyword"&gt;row&lt;/span&gt; * nc &amp;#43; col&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;);
                            grid[&lt;span class="hljs-keyword"&gt;row&lt;/span&gt;][col&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
                        }
                    }
                }
            }
        }

        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; num_ispools;
    }

    &lt;span class="hljs-built_in"&gt;public&lt;/span&gt; static &lt;span class="hljs-type"&gt;void&lt;/span&gt; main(String[] args) {
        Scanner scanner &amp;#61; &lt;span class="hljs-built_in"&gt;new&lt;/span&gt; Scanner(&lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;in&lt;/span&gt;);
        &lt;span class="hljs-type"&gt;int&lt;/span&gt; col &amp;#61; scanner.nextInt();
        &lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-type"&gt;line&lt;/span&gt; &amp;#61; scanner.nextInt();
        &lt;span class="hljs-type"&gt;int&lt;/span&gt;[][] arr&amp;#61; &lt;span class="hljs-built_in"&gt;new&lt;/span&gt; &lt;span class="hljs-type"&gt;int&lt;/span&gt;[col][&lt;span class="hljs-type"&gt;line&lt;/span&gt;];
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; i &amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;col;i&amp;#43;&amp;#43;){
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; j &amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;&lt;span class="hljs-type"&gt;line&lt;/span&gt;;&amp;#43;&amp;#43;j){
                arr[i][j] &amp;#61; scanner.nextInt();
            }
        }
        &lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;out&lt;/span&gt;.println(numIsPools(arr));
    }
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;已自测&amp;#xff0c;给个采纳吧&lt;/p&gt;

