using namespace std;
const int MAX_V=100;//顶点个数

vector&lt;int&gt;G[MAX_V+1];
int V,E;//顶点数，边数
bool used[MAX_V+1];

void dfs(int s)
{
    used[s]=true;
    printf("%d",s);
    for(int i=0;i&lt;G[s].size();i++)
    {
        if(!used[G[s][i]])//与这个点相连的点未被遍历
        {
            printf("-&gt;");
            dfs(G[s][i]);//从这个为被遍历的点开始往下找
        }
    }
}
void bfs(int s)
{
    queue&lt;int&gt;que;
    memset(used,false,sizeof(used));
    que.push(s);
    used[s]=true;
    while(!que.empty())
    {
        int v=que.front();//取出队首元素开始遍历与它相连的点
        que.pop();
        printf("%d",v);
        for(int i=0;i&lt;G[v].size();i++)
        {
            if(!used[G[v][i]])//先遍历与这个点相连且未被遍历过的点
            {
                que.push(G[v][i]);//将这些点加入队列
                used[G[v][i]]=true;
            }
        }
        if(!que.empty())
        {
            printf("-&gt;");
        }
    }
    printf("\n");
}
int main()
{
    while(~scanf("%d %d",&amp;V,&amp;E))
    {
        for(int i=1;i&lt;=V;i++)//邻接表初始化
        {
            G[i].clear();
        }
        int from,to;

        for(int i=0;i&lt;E;i++)
        {
            scanf("%d %d",&amp;from,&amp;to);
            //无向图双向边
            G[from].push_back(to);
            G[to].push_back(from);
        }

        memset(used,false,sizeof(used));//dfs就是从一个点出发若能往前遍历就往前遍历
        dfs(1);                         //不能遍历再往回退一个点，从这个点的另一方向往前遍历
        printf("\n");                   //持续上述过程直到所有的点都被遍历
        
        bfs(1);                         //bfs就是从一个点出发，先遍历它周围的点
                                        //再按遍历过的点顺序，遍历下一个点的周围的点
                                        //比如从1出发先遍历了2，3，4下一次就从2开始遍历周围的点
                                        //持续这样直到所有的点都被遍历
    }
    return 0;
}

