int search(int t,int x[],int n)
{
	int low=(n-1)/2;

	if( x &gt; x+n ) return -1;
	if( t &gt; x[low] )
	{
	    return search(t,x+low+1,n-low-1) + low +1;
	}
	else if( t &lt; x[low] ){
	    return search(t,x,low);
	}
	else
	    return low; 
}
