vector&lt;vector&lt;int&gt;&gt; fourSum(vector&lt;int&gt;&amp; nums, int target)
    {
        int n = nums.size();
        QuickSort(nums,0,n-1);

        int i,j,k,s,sum,t1,t2;
        vector&lt;int&gt; turple;
        vector&lt;vector&lt;int&gt;&gt; result;
        if(n &lt; 4)
            return result;
        t1 = nums[0];
        for(i=0;i&lt;n-3;i++)
        {
            if(i &amp;&amp; t1 == nums[i])
                continue;
            t2 = nums[i+1];
            for(j=i+1;j&lt;n-2;j++)
            {
                if(t2 == nums[j] &amp;&amp; j &gt;i+1)
                    continue;
                sum = target - nums[i] - nums[j];
                k = j+1;
                s = n-1;
                while(k&lt;s)
                {
                    if(nums[k] + nums[s] == sum)
                    {
                        turple.push_back(nums[i]);
                        turple.push_back(nums[j]);
                        turple.push_back(nums[k]);
                        turple.push_back(nums[s]);
                        result.push_back(turple);
                        turple.clear();
                        k++;
                        s--;
                    }
                    else if(nums[k] + nums[s] &lt; sum)
                        k++;
                    else
                        s--;
                 }
                t2 = nums[j];
             }
            t1 = nums[i];
        }
        result.erase(unique(result.begin(),result.end()),result.end());
        return result;
    }
    void QuickSort(vector&lt;int&gt;&amp;nums,int low,int high)
    {
        if(low&lt;high)
        {
            int pivot = nums[low];
            int first = low;
            int last = high;
            while(first&lt;last)
            {
                while(nums[last] &gt; pivot &amp;&amp; last &gt; first)
                {
                     --last;
                }
                nums[first] = nums[last];
                while(nums[first] &lt;= pivot &amp;&amp; last &gt;first)
                {
                    ++first;
                }
                nums[last] = nums[first];
            }
            nums[first] = pivot;
            QuickSort(nums,low,first-1);
            QuickSort(nums,first+1,high);
        }
        else
            return;
    }

