    data := []int{27, 15, 8, 9, 12, 4, 17, 19, 21, 23, 25}
    sort.Ints(data)
    fmt.Println(data)
    x := 9
    notpresent := false
    i := sort.Search(len(data), func(i int) bool { return data[i] &amp;gt;= x })
    if i &amp;gt;= len(data) || data[i] != x {
        // x is not present in data,
        // but i is the index where it would be inserted.
        notpresent = true
    }
    fmt.Println(x, notpresent)
}
