int resolve(int n, int m) {
	int b = 0, c = 0;
	int temp = m - n;
	int sum = 0;
	  
	if ((temp % 4) &gt; n) return 0;
	
	for (int c = (temp/4); c &gt;= 0; --c) {
		b = temp - (c * 4);
		if (b + c &lt;= n)
			sum += 1;
		else
			break;
	}

	return sum;

}
 
