    int i,j;
    lenSS = lengthStr(subStr);
    next = (int *)malloc(sizeof (int ) * lenSS);
    computeNext(subStr,next);
    lenMS = lengthStr(mainStr);
    i = 0;
    j = 0;
    while (i<lenMS-lenSS+1){
        while (j<lenSS)
        {
            if (mainStr[i] == subStr[j]){
                i++;
                j++;
            } else{
                j = next[j];
                if (j == -1) {j = 0;i = i+1;}
            }
        }
        return i-j;
    }
    return -1;
}
