&lt;p&gt;You can use another function that modifies the router to do that.&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;//In another file
func addSignHandler(r *mux.Router) {
    r.HandleFunc("/signup", signupGetHandler).Methods("GET")
    r.HandleFunc("/signup", signupPostHandler).Methods("POST")
    r.HandleFunc("/signin", signinGetHandler).Methods("GET")
    r.HandleFunc("/signin", signinPostHandler).Methods("POST")
    r.HandleFunc("/signout", signoutGetHandler).Methods("GET")
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;And to use it:&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;func NewRouter() *mux.Router {
    r := mux.NewRouter()
    r.HandleFunc("/", middleware.AuthRequired(indexGetHandler)).Methods("GET")
    r.HandleFunc("/", middleware.AuthRequired(indexPostHandler)).Methods("POST")

    addSignHandler(r)

    r.HandleFunc("/services", middleware.AuthRequired(servicesHandler)).Methods("GET")
    fs := http.FileServer(http.Dir("./static/"))
    r.PathPrefix("/static/").Handler(http.StripPrefix("/static/", fs))
    return r
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;Or even better, you can refactor your code to make it more consistent:&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;func addMainHandler(r *mux.Router) {
    r.HandleFunc("/", middleware.AuthRequired(indexGetHandler)).Methods("GET")
    r.HandleFunc("/", middleware.AuthRequired(indexPostHandler)).Methods("POST")
    r.HandleFunc("/services", middleware.AuthRequired(servicesHandler)).Methods("GET")
    fs := http.FileServer(http.Dir("./static/"))
    r.PathPrefix("/static/").Handler(http.StripPrefix("/static/", fs))
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;And simplify the &lt;code&gt;NewRouter&lt;/code&gt; to:&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;func NewRouter() *mux.Router {
    r := mux.NewRouter()
    addMainHandler(r)
    addSignHandler(r)
    return r
}
&lt;/code&gt;&lt;/pre&gt;
