int luck(int n)
{
  while (n &gt; 0)
  {  
    if (n % 10 == 7) return 1;
    n /= 10;
  }
  return 0;
}
int main()
{
int a, b;
scanf("%d%d", &amp;a, &amp;b);
for (int i = a; i &lt;= b; i++)
{
if (luck(i)) printf("%d ", i);
}
return 0;
}
