#include &lt;iostream&gt;
#include &lt;algorithm&gt;

using namespace std;

#define max(a,b) ((a)&gt;(b)?(a):(b))

int main(){
    int n, m;
    int a[100], b[5], call[100] = {0};
    bool choose[100] = {0};
	
    cin &gt;&gt; n;
    for( int i = 0; i &lt; n; i++ )
        cin &gt;&gt; a[i];
	
    cin &gt;&gt; m;
    for( int i = 0; i &lt; m; i++ ){
        memset(choose, false, 100);
        memset(call, 0, 100);
			
        for(int j = 0; j &lt; 5; j++){
            cin &gt;&gt; b[j];
            choose[b[j]] = true;	
        }
				
        for(int j = 0; j &lt; 5; j++){
            int temp = b[j];
            while(a[temp]!=temp &amp;&amp; choose[a[temp]]){
                call[a[temp]]++;
                temp = a[temp];
            }
        }
        
        int mCall = 0;
        for( int j = 0; j &lt; 5; j++ ){
            mCall = max(mCall, call[b]);
        }
        cout &lt;&lt; mCall + 1 &lt;&lt; endl;
    }
	
    return 0;
} 
