public static int Abc(int input1, int input2, int output,
			String methodName, int from, int to) {
		int count = 0;
		String prefix = "D";
		for(int i=from; i&lt;=to; i++) {
			try {
				Class clazz = Class.forName(prefix + i);
				Object instance = clazz.newInstance();
				Method method = clazz.getDeclaredMethod(methodName, Integer.class, Integer.class);
				Integer res = (Integer)method.invoke(instance, input1, input2);
				if(res == output) {
					count++;
					System.out.println("pick " + prefix + i);
				}
			} catch (Exception e) {
			}
		}
		return count;
	}

