		else{
			pc-&gt;next = new Node(pb-&gt;data.xishu, pb-&gt;data.mi);
			++sum.num;
			if(sum.highestdigit&lt;pb-&gt;data.mi) sum.highestdigit = pb-&gt;data.mi;
			pc = pc-&gt;next;
			pb = pb-&gt;next;
		}
	}
	while (pa){
		pc-&gt;next = new Node(pa-&gt;data.xishu, pa-&gt;data.mi);
		++sum.num;
		if(sum.highestdigit&lt;pa-&gt;data.mi) sum.highestdigit = pa-&gt;data.mi;
		pc = pc-&gt;next;
		pa = pa-&gt;next;
	}
	while (pb){
		pc-&gt;next = new Node(pb-&gt;data.xishu, pb-&gt;data.mi);
		++sum.num;
		if(sum.highestdigit&lt;pb-&gt;data.mi) sum.highestdigit = pb-&gt;data.mi;
		pc = pc-&gt;next;
		pb = pb-&gt;next;
	}
	return sum;
}
