&lt;span class="hljs-meta"&gt;#include &amp;lt;stdio.h&amp;gt;&lt;/span&gt;
//定义结构体类型Range
typedef struct _Range {
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-keyword"&gt;start&lt;/span&gt;, &lt;span class="hljs-keyword"&gt;end&lt;/span&gt;;
} Range;
//获得结构体变量的函数&amp;#xff1a;它表示一个数组中的区间&amp;#xff0c;由起始点和结束点构成
Range new_Range(&lt;span class="hljs-type"&gt;int&lt;/span&gt; s, &lt;span class="hljs-type"&gt;int&lt;/span&gt; e) {
    Range r;
    r.start &amp;#61; s;
    r.end &amp;#61; e;
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; r;
}
//将x指向的元素和y指向的元素交换顺序&amp;#xff1a;如果基准点左侧某个元素大于基准点且右侧有一个元素小于基准点&amp;#xff0c;交换这两个元素
&lt;span class="hljs-type"&gt;void&lt;/span&gt; swap(&lt;span class="hljs-type"&gt;int&lt;/span&gt; *x, &lt;span class="hljs-type"&gt;int&lt;/span&gt; *y) {
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; t &amp;#61; *x;
    *x &amp;#61; *y;
    *y &amp;#61; t;
}
//快速排序的方法
&lt;span class="hljs-type"&gt;void&lt;/span&gt; quick_sort(&lt;span class="hljs-type"&gt;int&lt;/span&gt; arr[], const &lt;span class="hljs-type"&gt;int&lt;/span&gt; len) {
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (len &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt;; // 避免len等於負值時引發段錯誤&amp;#xff08;Segment Fault&amp;#xff09;&amp;#xff0c;排除要排序的数组的元素个数为&lt;span class="hljs-number"&gt;0&lt;/span&gt;或负值
    //技巧&amp;#xff1a; r[]模擬列表,p為數量,r[p&amp;#43;&amp;#43;]為push&amp;#xff08;它先使用,r[&lt;span class="hljs-comment"&gt;--p]為定位到前一个元素并取出该元素&lt;/span&gt;
    Range r[len];//声明待排序数组的元素区间组成的数组&amp;#xff0c;区间个数等于要排序的数组
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; p &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;//区间数组的初始索引为&lt;span class="hljs-number"&gt;0&lt;/span&gt;
    r[p&amp;#43;&amp;#43;] &amp;#61; new_Range(&lt;span class="hljs-number"&gt;0&lt;/span&gt;, len - &lt;span class="hljs-number"&gt;1&lt;/span&gt;);//表示要排序数组从第一个元素到最后一个元素的索引区间&amp;#xff0c;并存入区间数组
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (p) {//如果有一个及以上的区间就进入循环直到没有区间循环终止
        Range range &amp;#61; r[&lt;span class="hljs-comment"&gt;--p];//取出刚存入的待排数组地元素区间&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (range.&lt;span class="hljs-keyword"&gt;start&lt;/span&gt; &amp;gt;&amp;#61; range.&lt;span class="hljs-keyword"&gt;end&lt;/span&gt;)
            &lt;span class="hljs-keyword"&gt;continue&lt;/span&gt;;//但元素区间起始索引大于终了索引&amp;#xff0c;这个区间无效&amp;#xff0c;继续使用下一个元素区间&amp;#xff1b;
        &lt;span class="hljs-type"&gt;int&lt;/span&gt; mid &amp;#61; arr[(range.&lt;span class="hljs-keyword"&gt;start&lt;/span&gt; &amp;#43; range.&lt;span class="hljs-keyword"&gt;end&lt;/span&gt;) / &lt;span class="hljs-number"&gt;2&lt;/span&gt;]; &lt;span class="hljs-comment"&gt;/* 取出该有效元素区间地中间值对应地元素的值作为基准&amp;#xff0c;
        该基准将元素区间划分成左右两部分*/&lt;/span&gt;
        &lt;span class="hljs-type"&gt;int&lt;/span&gt; left &amp;#61; range.&lt;span class="hljs-keyword"&gt;start&lt;/span&gt;, right &amp;#61; range.&lt;span class="hljs-keyword"&gt;end&lt;/span&gt;;&lt;span class="hljs-comment"&gt;/*有效元素区间的起始值作为左部序列的开始元素索引
        有效元素区间的终了值作为右部序列的结束元素的索引*/&lt;/span&gt;

        &lt;span class="hljs-keyword"&gt;do&lt;/span&gt;
        {
            &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (arr[left] &amp;lt; mid) &amp;#43;&amp;#43;left;   &lt;span class="hljs-comment"&gt;/*对基准点左部的元素向右扫描直到左部序列的某个元素大于基准点&amp;#xff0c;
            这样就将小于基准的元素集中在元素区间的左部&amp;#xff0c;left是向右移动的指针*/&lt;/span&gt;
            &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (arr[right] &amp;gt;&amp;#61; mid) &lt;span class="hljs-comment"&gt;--right; /*对基准点右部的元素向左扫描直到右部序列的某个元素小于基准点&amp;#xff0c;&lt;/span&gt;
            将大于或等于基准的元素集中在元素区间的右部&amp;#xff0c;right是向左移动的指针*/

            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (left &amp;lt;&amp;#61; right)&lt;span class="hljs-comment"&gt;/*当左部序列扫描结束之时的元素依然在右部序列扫描结束之时的元素之前*/&lt;/span&gt;
            {
                swap(&amp;amp;arr[left],&amp;amp;arr[right]);//将左部序列扫描结束之时的元素和右部序列扫描结束之时的元素进行交换
                left&amp;#43;&amp;#43;;right&lt;span class="hljs-comment"&gt;--;               /*接着向右扫描左部序列&amp;#xff0c;向左扫描右部序列&amp;#xff0c;重复上面的步骤*/ &lt;/span&gt;
            }
        } &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (left &amp;lt;&amp;#61; right);&lt;span class="hljs-comment"&gt;/*向右扫描左部序列和向左扫描右部序列直到相遇&amp;#xff1a;相同索引的元素交换彼此的值&amp;#xff1b;之后向
        右扫描到了右部序列&amp;#xff0c;向左扫描到了左部序列&amp;#xff0c;基准点左部的各元素都小于它&amp;#xff0c;右部的的各元素都大于等于它&amp;#xff0c;循环结束*/&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (range.&lt;span class="hljs-keyword"&gt;start&lt;/span&gt; &amp;lt; right) r[p&amp;#43;&amp;#43;] &amp;#61; new_Range(range.&lt;span class="hljs-keyword"&gt;start&lt;/span&gt;, right);&lt;span class="hljs-comment"&gt;/*上面的工作完成之后&amp;#xff0c;right指针
        遇到left或者right再left左边紧邻&amp;#xff1b;只是现在将原来元素区间的起始点和right指针现在的位置构成新的元素区间&amp;#xff0c;
        然后通过一个基准点划分该区间为左部和右部&amp;#xff0c;使得左部序列的元素小于右部序列的元素
        */&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (range.&lt;span class="hljs-keyword"&gt;end&lt;/span&gt; &amp;gt; left) r[p&amp;#43;&amp;#43;] &amp;#61; new_Range(left, range.&lt;span class="hljs-keyword"&gt;end&lt;/span&gt;);&lt;span class="hljs-comment"&gt;/*上面的工作完成之后&amp;#xff0c;left指针
        遇到right或者再righ后面与之紧邻&amp;#xff1b;只是现在将left指针现在的元素位置和原来元素区间的终了点构成新的元素区间&amp;#xff0c;
        然后通过一个基准点划分该区间为左部和右部&amp;#xff0c;使得左部序列的元素小于右部序列的元素*/&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;/*左部序列向右扫描&amp;#xff0c;右部序列向左扫描&amp;#xff0c;肯定会在某个索引处相遇或者错开b&amp;#xff0c;原来的基准在其中的一个区间中&amp;#xff0c;之后从该根据right和left两个索引
        划分两个区间&amp;#xff0c;存到区间数组中&amp;#xff1b;
        先完成右部序列的快速排序&amp;#xff0c;再完成左部序列的快速排序&amp;#xff0c;最终能得到数组的排序*/&lt;/span&gt;
    }

}
&lt;span class="hljs-type"&gt;int&lt;/span&gt; main()
{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; arr[&lt;span class="hljs-number"&gt;6&lt;/span&gt;] &amp;#61;{&lt;!-- --&gt;&lt;span class="hljs-number"&gt;10&lt;/span&gt;,&lt;span class="hljs-number"&gt;5&lt;/span&gt;,&lt;span class="hljs-number"&gt;9&lt;/span&gt;,&lt;span class="hljs-number"&gt;7&lt;/span&gt;,&lt;span class="hljs-number"&gt;24&lt;/span&gt;,&lt;span class="hljs-number"&gt;18&lt;/span&gt;};
    quick_sort(arr,&lt;span class="hljs-number"&gt;6&lt;/span&gt;);
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; ( i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-number"&gt;6&lt;/span&gt;; i&amp;#43;&amp;#43;)
    {
        printf(&amp;#34;%d\n&amp;#34;,arr[i]);
    }
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;

}
&lt;/code&gt;&lt;/pre&gt;

