{ // è¿ç®—ç¬¦ä¼˜å…ˆçº§è¡¨   
	// '+' '-' '*' '/' '(' ')' '#' '^'   
	/*'+'*/'&gt;','&gt;','&lt;','&lt;','&lt;','&gt;','&gt;','&lt;',   
